import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const GamePage = () => {
    const { gameId } = useParams();
    const [gameData, setGameData] = useState(null);
    const [error, setError] = useState(null);
    const [secretNumber, setSecretNumber] = useState('');
    const [guess, setGuess] = useState('');
    const [turn, setTurn] = useState(0);  // 0 for player1, 1 for player2
    const [guesses, setGuesses] = useState([]);
    const [message, setMessage] = useState('');

    // Fetch game data from the server when the component mounts or the gameId changes
    useEffect(() => {
        axios
            .get(`http://localhost:5000/api/games/${gameId}`)
            .then(response => {
                setGameData(response.data);
            })
            .catch(err => {
                console.error('Error fetching game data:', err.response || err);
                setError('Error fetching game data');
            });
    }, [gameId]);

    if (error) return <div>{error}</div>;
    if (!gameData) return <div>Loading...</div>;

    const handleSetSecretNumber = (number) => {
        // Only allow Player 1 to set the secret number
        if (gameData.players[turn] === gameData.players[0]) {
            axios.post(`http://localhost:5000/api/games/secret`, { gameId, secretNumber: number })
                .then(response => {
                    setMessage('Secret number set!');
                })
                .catch(err => {
                    console.error('Error setting secret number:', err);
                });
        }
    };

    const handleMakeGuess = (guess, setTurn) => {
        // Make the guess and change turn
        axios.post(`http://localhost:5000/api/games/guess`, { gameId, guess })
            .then(response => {
                setGuesses([...guesses, guess]);
                setTurn((turn + 1) % 2); // Change turn between players
                setMessage(response.data.message);
            })
            .catch(err => {
                console.error('Error making guess:', err);
            });
    };

    return (
        <div>
            <h1>Game: {gameData.gameId}</h1>
            <p>Players: {gameData.players.join(', ')}</p>
            <p>Status: {gameData.status}</p>

            {/* Display input section for setting secret number for Player 1 only */}
            {gameData.players[turn] === gameData.players[0] ? (
                <div>
                    <h2>Set a secret number (4-6 unique digits):</h2>
                    <input
                        type="text"
                        value={secretNumber}
                        onChange={(e) => setSecretNumber(e.target.value)}
                        placeholder="Enter secret number"
                    />
                    <button onClick={() => handleSetSecretNumber(secretNumber)}>Set Secret Number</button>
                </div>
            ) : (
                <p>Waiting for Player 1 to set the secret number...</p>
            )}

            {/* Display guessing section */}
            {gameData.status === 'in-progress' && (
                <div>
                    <h2>Player {turn + 1}, make your guess:</h2>
                    <input
                        type="text"
                        value={guess}
                        onChange={(e) => setGuess(e.target.value)}
                        placeholder="Enter your guess"
                    />
                    <button onClick={() => handleMakeGuess(guess, setTurn)}>Submit Guess</button>
                </div>
            )}

            <div>
                <h3>Messages:</h3>
                <p>{message}</p>
            </div>

            <div>
                <h3>Previous Guesses:</h3>
                {guesses.map((g, index) => (
                    <p key={index}>Guess {index + 1}: {g}</p>
                ))}
            </div>
        </div>
    );
};

export default GamePage;
