// src/components/JoinGame.js
import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './JoinGame.css';
import { ReactComponent as LoadingSpinner } from '../assets/loading-spinner.svg';

const JoinGame = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [playerName, setPlayerName] = useState(location.state?.playerName || '');
    const [gameId, setGameId] = useState('');
    const [message, setMessage] = useState('');
    const [isJoining, setIsJoining] = useState(false);

    useEffect(() => {
        if (!playerName) {
            navigate('/');
        }
    }, [playerName, navigate]);

    const handleJoinGame = async () => {
        if (!gameId) {
            setMessage('Please enter the game code');
            return;
        }
        setIsJoining(true);
        try {
            // Remove any dashes from the game ID before sending
            const cleanGameId = gameId.replace(/-/g, '');
            const response = await axios.post('http://localhost:5000/api/games/join', {
                gameId: cleanGameId,
                playerName
            });

            const game = response.data.game;
            setMessage(response.data.message);

            if (game.players.length === 2 && game.status === 'waitingForSecret') {
                navigate(`/${playerName}/setsecretcode/${cleanGameId}`);
            }
        } catch (err) {
            console.error('Error joining game:', err);
            setMessage(err.response?.data?.message || 'Error joining game');
        } finally {
            setIsJoining(false);
        }
    };

    const formatGameId = (id) => {
        const cleanId = id.replace(/[^A-Z0-9]/g, '').substring(0, 6);
        return cleanId.match(/.{1,3}/g)?.join('-') || cleanId;
    };

    return (
        <div className="join-game-container">
            <nav className="nav-bar">
                <h1 className="logo">NUMBER<span className="logo-accent">POSITION</span></h1>
            </nav>

            <main className="main-content">
                <div className="game-card">
                    <div className="join-panel">
                        <h2>Join Existing Game</h2>
                        <div className="player-info">
                            <span className="player-label">Player Name:</span>
                            <span className="player-name">{playerName}</span>
                        </div>

                        <div className="input-group">
                            <input
                                type="text"
                                placeholder="Enter Game Code (e.g. ABC-123)"
                                value={formatGameId(gameId)}
                                onChange={(e) => {
                                    // Sanitize input and store raw value
                                    const cleanValue = e.target.value
                                        .replace(/[^A-Z0-9]/g, '')
                                        .substring(0, 6)
                                        .toUpperCase();
                                    setGameId(cleanValue);
                                }}
                                className="code-input"
                                maxLength="7" // Allows for 6 chars + dash
                            />
                        </div>

                        <button
                            className="btn join-btn"
                            onClick={handleJoinGame}
                            disabled={isJoining}
                        >
                            {isJoining ? (
                                <LoadingSpinner className="spinner" />
                            ) : (
                                'Join Game'
                            )}
                        </button>

                        {message && (
                            <div className={`alert ${message.includes('Error') ? 'error' : 'info'}`}>
                                {message}
                            </div>
                        )}
                    </div>
                </div>
            </main>

            <footer className="footer">
                <p className="copyright">
                    Â© {new Date().getFullYear()} Number Position Game. All rights reserved.
                </p>
            </footer>
        </div>
    );
};

export default JoinGame;
