import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './CreateGame.css';

const CreateGame = () => {
    const { state } = useLocation();
    const [playerName, setPlayerName] = useState(state?.playerName || '');
    const [gameId, setGameId] = useState(state?.gameId || '');
    const [isPlayerJoined, setIsPlayerJoined] = useState(false);  // Placeholder for join confirmation
    const navigate = useNavigate();

    useEffect(() => {
        if (!state) {
            alert('No player information found!');
            navigate('/');
        }
    }, [state, navigate]);

    useEffect(() => {
        // Simulate automatic join for testing
        const timeout = setTimeout(() => {
            setIsPlayerJoined(true);  // Set player joined immediately
        }, 1000);  // Simulate a short wait before confirming join
        return () => clearTimeout(timeout);
    }, []);


    const handleNextStep = () => {
        navigate(`/game/${gameId}`);
    };

    return (
        <div className="waiting-room">
            <h1>Waiting Room</h1>
            <p>Game ID: <strong>{gameId}</strong></p>
            <p>Player: <strong>{playerName}</strong></p>
            {!isPlayerJoined ? (
                <p>Waiting for another player to join...</p>
            ) : (
                <>
                    <p>Another player has joined! Ready to start.</p>
                    <button className="button" onClick={handleNextStep}>Proceed to Game</button>
                </>
            )}
        </div>
    );
};

export default CreateGame;
