import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useParams } from 'react-router-dom';

const GamePage = () => {
    const { gameId } = useParams();
    const { state } = useLocation();
    const [gameData, setGameData] = useState(null);
    const [playerName, setPlayerName] = useState(state ? state.playerName : '');
    const [error, setError] = useState(null);
    const [secretNumber, setSecretNumber] = useState('');
    const [guess, setGuess] = useState('');
    const [turn, setTurn] = useState(0);  // 0 for player1, 1 for player2
    const [guesses, setGuesses] = useState([]);
    const [message, setMessage] = useState('');

    // Fetch game data and automatically join the game if it's the joining player's turn
    useEffect(() => {
        axios
            .get(`http://localhost:5000/api/games/${gameId}`)
            .then(response => {
                setGameData(response.data);
                // Only allow joining if there is exactly 1 player
                if (response.data.players.length === 1) {
                    setMessage('Waiting for Player 2 to join...');
                } else if (response.data.players.length === 2) {
                    setMessage('Both players have joined. The game is starting!');
                }
            })
            .catch(err => {
                console.error('Error fetching game data:', err);
                setMessage('Error fetching game data');
            });
    }, [gameId]);





    // Update the game status in the UI
    function updateGameStatus(game) {
        const gameStatusElement = document.getElementById('game-status');
        const playersElement = document.getElementById('players');

        // Display game status
        gameStatusElement.textContent = game.status;
        playersElement.textContent = `Players: ${game.player1} / ${game.player2}`;

        // Show relevant messages based on status
        if (game.status === 'waitingForPlayer1') {
            document.getElementById('message').textContent = 'Waiting for Player 1 to set their secret number...';
        } else if (game.status === 'waitingForPlayer2') {
            document.getElementById('message').textContent = 'Waiting for Player 2 to set their secret number...';
        }
    }

    const handleJoinGame = () => {
        if (!playerName) {
            console.error('Player name is not defined!');
            setMessage('Player name is required!');
            return;
        }

        // Check if game already has 2 players before joining
        if (gameData.players.length === 2) {
            console.log('Game already has two players');
            setMessage('Game already has two players.');
            return;
        }

        console.log('Joining game with the following data:', { gameId, playerName });

        axios.post(`http://localhost:5000/api/games/join`, { gameId, player: playerName })
            .then(response => {
                console.log('Player joined the game:', response.data);
                setMessage('Player joined the game!');
                setGameData(prevData => ({
                    ...prevData,
                    players: [...prevData.players, playerName],  // Add player to the list
                    status: 'in-progress', // Update the status if two players are present
                }));

                // If both players are now in the game, update status
                if (gameData.players.length === 2) {
                    setMessage('Both players are now in the game. The game will start.');
                }
            })
            .catch(err => {
                console.error('Error joining the game:', err);
                setMessage('Error joining the game');
            });
    };

    const isValidSecretNumber = (number) => {
        const digits = number.split('');
        return digits.length === 4 && new Set(digits).size === 4 && /^\d{4}$/.test(number);
    };

    const handleSetSecretNumber = (number) => {
        if (isValidSecretNumber(number)) {
            axios.post(`http://localhost:5000/api/games/secret`, { gameId, secretNumber: number, player: gameData.players[turn] })
                .then(response => {
                    setMessage('Secret number set!');
                    setGameData(prevData => ({
                        ...prevData,
                        [gameData.players[turn] === gameData.players[0] ? 'secretNumber1' : 'secretNumber2']: number
                    }));
                })
                .catch(err => {
                    console.error('Error setting secret number:', err.response ? err.response.data : err);
                    setMessage(`Error setting secret number: ${err.response ? err.response.data.message : err.message}`);
                });
        } else {
            setMessage('Secret number must be 4 unique digits.');
        }
    };

    const handleMakeGuess = (guess) => {
        axios.post(`http://localhost:5000/api/games/guess`, { gameId, guess, player: gameData.players[turn] })
            .then(response => {
                setGuesses([...guesses, guess]);
                setTurn((turn + 1) % 2); // Change turn between players
                setMessage(response.data.message);
            })
            .catch(err => {
                console.error('Error making guess:', err);
                setMessage('Error making guess');
            });
    };

    if (error) return <div>{error}</div>;
    if (!gameData) return <div>Loading...</div>;

    return (
        <div>
            <h1>Game: {gameData.gameId}</h1>
            <p>Players: {gameData.players.join(', ')}</p>
            <p>Status: {gameData.status}</p>

            {/* Display input section for setting secret number */}
            {gameData.secretNumber1 && gameData.secretNumber2 ? (
                <p>Both players have set their secret numbers.</p>
            ) : (
                <div>
                    <h2>Set your secret number (4 unique digits):</h2>
                    <input
                        type="text"
                        value={secretNumber}
                        onChange={(e) => setSecretNumber(e.target.value)}
                        maxLength={4}
                        placeholder="Enter secret number"
                    />
                    <button onClick={() => handleSetSecretNumber(secretNumber)}>Set Secret Number</button>
                </div>
            )}

            {/* Display guessing section */}
            {gameData.status === 'in-progress' && (
                <div>
                    <h2>Player {turn + 1}, make your guess:</h2>
                    <input
                        type="text"
                        value={guess}
                        onChange={(e) => setGuess(e.target.value)}
                        placeholder="Enter your guess"
                    />
                    <button onClick={() => handleMakeGuess(guess)}>Submit Guess</button>
                </div>
            )}

            <div>
                <h3>Messages:</h3>
                <p>{message}</p>
            </div>

            <div>
                <h3>Previous Guesses:</h3>
                {guesses.map((g, index) => (
                    <p key={index}>Guess {index + 1}: {g}</p>
                ))}
            </div>
        </div>
    );
};

export default GamePage;
