import React, { useState } from 'react';
import GuessRow from './GuessRow';

function GuessBoard() {
    const [guesses, setGuesses] = useState([]);
    const [currentGuess, setCurrentGuess] = useState('');

    // Function to calculate N (correct digits in the correct position) and P (correct digits in the wrong position)
    const calculateNandP = (guess, secretCode) => {
        let n = 0;
        let p = 0;
        let secretArray = [...secretCode];
        let guessArray = [...guess];

        // Calculate N (correct digits in the correct position)
        for (let i = 0; i < guess.length; i++) {
            if (guess[i] === secretCode[i]) {
                n++;
                secretArray[i] = null; // Remove this matched digit from the secret code
                guessArray[i] = null;  // Remove this matched digit from the guess
            }
        }

        // Calculate P (correct digits in the wrong position)
        for (let i = 0; i < guessArray.length; i++) {
            if (guessArray[i] !== null && secretArray.includes(guessArray[i])) {
                p++;
                secretArray[secretArray.indexOf(guessArray[i])] = null; // Remove matched digit from secret code
            }
        }

        return { n, p };
    };

    // Handle guess submit event
    const handleGuessSubmit = (event) => {
        event.preventDefault();
        // Replace this with the actual logic to calculate N and P
        const secretCode = "1234"; // Example, replace with the actual secret code or fetch dynamically
        const { n, p } = calculateNandP(currentGuess, secretCode);
        setGuesses([...guesses, { guess: currentGuess, n, p }]);
        setCurrentGuess('');
    };

    return (
        <div>
            <h2>Game Board</h2>
            <form onSubmit={handleGuessSubmit}>
                <input
                    type="text"
                    value={currentGuess}
                    onChange={(e) => setCurrentGuess(e.target.value)}
                    placeholder="Enter your guess"
                    maxLength={4} // Example for a 4-digit game
                    required
                />
                <button type="submit">Submit Guess</button>
            </form>
            {guesses.map((entry, index) => (
                <GuessRow key={index} guess={entry.guess} n={entry.n} p={entry.p} />
            ))}
        </div>
    );
}

export default GuessBoard;
