    return (
        <div className="homepage">
            <h1>Welcome to the Number Position Game</h1>
            <p>
                In this game, each player has a secret number. Players take turns guessing each other's numbers, receiving feedback on the correct number and its position (N and P). The game continues until one player correctly guesses the otherâ€™s secret number.
            </p>
            <p>
                You can either create a new game or join an existing one to get started.
            </p>

            <div className="game-options">
                <Link to="/create-game" className="btn">Create Game</Link>
                <Link to="/join-game" className="btn">Join Game</Link>
            </div>
        </div>
    );
};

export default HomePage;
