import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import NumberHelper from './NumberHelper';
import Timer from './Timer';
import './GamePageCreator.css';

const GamePageCreator = () => {
    const { gameId } = useParams();
    const navigate = useNavigate();
    const [gameData, setGameData] = useState(null);
    const [guess, setGuess] = useState('');
    const [guessMessage, setGuessMessage] = useState('');
    const [turnTrigger, setTurnTrigger] = useState(null);

    useEffect(() => {
        const interval = setInterval(async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/games/${gameId}`);
                setGameData(res.data);
                setTurnTrigger(res.data.turn); // update trigger when turn changes
            } catch (err) {
                console.error('Error fetching game data:', err);
            }
        }, 3000);
        return () => clearInterval(interval);
    }, [gameId]);

    useEffect(() => {
        const checkTimeout = async () => {
            if (gameData?.timeRemaining <= 0 && myTurn && !finished) {
                try {
                    await axios.post('/api/games/timeout', { gameId });
                    setGuessMessage('Time is up! Turn skipped.');
                } catch (err) {
                    console.error('Error handling timeout:', err);
                }
            }
        };
        checkTimeout();
    }, [gameData]);

    if (!gameData) return <p>Loading game data...</p>;

    const myGuesses = (gameData.guesses ?? []).filter(g => g.player === gameData.players[0]);
    const oppGuesses = (gameData.guesses ?? []).filter(g => g.player === gameData.players[1]);
    const playerName = gameData.players[0];
    const myTurn = gameData.turn === playerName;
    const finished = gameData.status === 'finished';

    const handleSubmitGuess = async (e) => {
        e.preventDefault();
        if (!guess) {
            alert('Please enter a guess.');
            return;
        }
        try {
            const res = await axios.post('http://localhost:5000/api/games/guess', { gameId, player: playerName, guess });
            setGuessMessage(res.data.message);
            setGuess('');
        } catch (err) {
            console.error('Error submitting guess:', err);
            setGuessMessage(err.response?.data?.message || 'Error submitting guess');
        }
    };

    const onTimeUp = async () => {
        if (myTurn && gameData.status !== 'finished') {
            try {
                // Auto-submit an empty guess and mark the player as missed
                await axios.post('http://localhost:5000/api/games/guess', { gameId, player: playerName, guess: '' });
                setGuessMessage('Time is up! Turn skipped.');

                // Update the game to mark this guess as missed and set numbers/positions to '-'
                await axios.post('http://localhost:5000/api/games/update', {
                    gameId,
                    player: playerName,
                    guess: '-',
                    numbersCorrect: '-',
                    positionsCorrect: '-'
                });

                // Update turn to the next player
                await axios.post('http://localhost:5000/api/games/nextTurn', { gameId });

            } catch (err) {
                console.error('Error auto-submitting guess or switching turn:', err);
            }
        }
    };

    let outcomeMessage = '';
    if (finished) {
        if (gameData.winner === "draw") {
            outcomeMessage = "It's a draw!";
        } else if (gameData.winner === playerName) {
            outcomeMessage = "Congratulations! You win! Confetti!!!";
        } else {
            outcomeMessage = "You lost! Better luck next time!";
        }
    }

    const handleResetGame = async () => {
        try {
            // API call to reset game status, clear guesses, or restart
            await axios.post('http://localhost:5000/api/games/reset', { gameId });
            navigate('/');
        } catch (err) {
            console.error('Error resetting game:', err);
        }
    };

    return (
        <div>
            <h2>Player 1 Game Page</h2>
            <p>Your Secret Code: {gameData.secretCode1}</p>
            <p><strong>Turn:</strong> {gameData.turn ? gameData.turn : 'Not set'}</p>
            {!finished && <Timer initialTime={gameData.timeRemaining} onTimeUp={onTimeUp} resetTrigger={turnTrigger} />}
            {finished && (
                <div>
                    <h3>Game Over</h3>
                    <p>{outcomeMessage}</p>
                    <button onClick={() => navigate('/')}>Go to Home Page</button>
                    <button onClick={handleResetGame}>Restart Game</button>
                </div>
            )}
            {!finished && (
                <>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <div style={{ width: '48%' }}>
                            <h3>Your Guess History</h3>
                            {myGuesses.length === 0 ? (
                                <p>No guesses yet.</p>
                            ) : (
                                <table border="1">
                                    <thead>
                                        <tr>
                                            <th>Guess</th>
                                            <th>Numbers Correct</th>
                                            <th>Positions Correct</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {myGuesses.map((g, index) => (
                                            <tr key={index}>
                                                <td>{g.guess}</td>
                                                <td>{g.numbersCorrect}</td>
                                                <td>{g.positionsCorrect}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            )}
                        </div>
                        <div style={{ width: '48%' }}>
                            <h3>Opponent's Guess History</h3>
                            {oppGuesses.length === 0 ? (
                                <p>No guesses yet.</p>
                            ) : (
                                <table border="1">
                                    <thead>
                                        <tr>
                                            <th>Guess</th>
                                            <th>Numbers Correct</th>
                                            <th>Positions Correct</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {oppGuesses.map((g, index) => (
                                            <tr key={index}>
                                                <td>{g.guess}</td>
                                                <td>{g.numbersCorrect}</td>
                                                <td>{g.positionsCorrect}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            )}
                        </div>
                    </div>
                    {myTurn ? (
                        <form onSubmit={handleSubmitGuess}>
                            <input
                                type="text"
                                placeholder="Enter your guess"
                                value={guess}
                                onChange={(e) => setGuess(e.target.value)}
                            />
                            <button type="submit">Submit Guess</button>
                        </form>
                    ) : (
                        <p>Waiting for your turn...</p>
                    )}
                    <NumberHelper />
                    {guessMessage && <p>{guessMessage}</p>}
                </>
            )}
        </div>
    );
};

export default GamePageCreator;
