// src/components/HowToPlay.js
import React from 'react';
import { Link } from 'react-router-dom';
import './HowToPlay.css';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { docco } from 'react-syntax-highlighter/dist/esm/styles/hljs';

// Import sample images (replace with your actual screenshots)
const signupCode = `import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Auth.css'; // Ensure this file contains the necessary styles

const Signup = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: ''
    });
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const validateEmail = (email) => {
        return /^[a-zA-Z0-9._%+-]+@gmail\.com$/.test(email);
    };

    const validateUsername = (username) => {
        return /^[a-zA-Z0-9_]+$/.test(username);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        if (!validateEmail(formData.email)) {
            setError('We only support @gmail.com emails.');
            return;
        }

        if (!validateUsername(formData.username)) {
            setError('Username can only contain A-Z, 0-9, and underscores.');
            return;
        }

        try {
            const res = await axios.post('http://localhost:5000/api/auth/signup', formData);
            localStorage.setItem('token', res.data.token);
            localStorage.setItem('user', JSON.stringify(res.data.user));
            navigate('/');
        } catch (err) {
            console.error(err);
            setError(err.response?.data?.message || 'Signup failed');
        }
    };

    return (
        <div className="auth-container">
            <h2>Sign Up</h2>
            {error && <p className="error-msg">{error}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="username"
                    placeholder="Username"
                    value={formData.username}
                    onChange={handleChange}
                    required
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Sign Up</button>
            </form>
            <p>
                Already have an account? <Link to="/login">Log In</Link>
            </p>
        </div>
    );
};

export default Signup;`;

const loginCode = `// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Auth.css';

const Login = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        try {
            const res = await axios.post('http://localhost:5000/api/auth/login', formData);
            localStorage.setItem('token', res.data.token);
            localStorage.setItem('user', JSON.stringify(res.data.user));
            navigate('/');
        } catch (err) {
            console.error(err);
            setError(err.response?.data?.message || 'Login failed');
        }
    };

    return (
        <div className="auth-container">
            <h2>Log In</h2>
            {error && <p className="error-msg">{error}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Log In</button>
            </form>
            <p>
                Don't have an account? <Link to="/signup">Sign Up Here</Link>
            </p>
        </div>
    );
};

export default Login;
`;

const createGameCode = `// src/components/CreateGame.js
import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './CreateGame.css';
import { ReactComponent as LoadingSpinner } from '../assets/loading-spinner.svg';


const CreateGame = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [playerName, setPlayerName] = useState(location.state?.playerName || '');
    const [gameId, setGameId] = useState('');
    const [message, setMessage] = useState('');
    const [isCreating, setIsCreating] = useState(false);


    useEffect(() => {
        if (!playerName) {
            navigate('/');
        }
    }, [playerName, navigate]);

    const handleCreateGame = async () => {
        setIsCreating(true);
        try {
            const response = await axios.post('http://localhost:5000/api/games', { playerName });
            setGameId(response.data.gameId);
            setMessage(response.data.message);
        } catch (err) {
            console.error('Error creating game:', err);
            setMessage('Error creating game. Please try again.');
        } finally {
            setIsCreating(false);
        }
    };

    const nextQuestion = () => {
        const nextIndex = (currentQuestion + 1) % questions.length;
        setCurrentQuestion(nextIndex);
    };

    return (
        <div className="create-game-container">
            <nav className="nav-bar">
                <h1 className="logo">NUMBER<span className="logo-accent">POSITION</span></h1>
            </nav>

            <main className="main-content">
                <div className="game-card">
                    {!gameId ? (
                        <div className="creation-panel">
                            <h2>Create New Game</h2>
                            <div className="player-info">
                                <span className="player-label">Player Name:</span>
                                <span className="player-name">{playerName}</span>
                            </div>
                            <button
                                className="btn create-btn"
                                onClick={handleCreateGame}
                                disabled={isCreating}
                            >
                                {isCreating ? (
                                    <LoadingSpinner className="spinner" />
                                ) : (
                                    'Generate Game Code'
                                )}
                            </button>
                            {message && <div className="alert error">{message}</div>}
                        </div>
                    ) : (
                        <div className="waiting-panel">
                            <h2>Game Created!</h2>
                            <div className="game-id-display">
                                <span className="label">Game Code:</span>
                                <span className="code">{gameId}</span>
                            </div>
                            <p className="instructions">
                                Share this code with Player 2 to start the game.
                            </p>
                            <div className="status-indicator">
                                <LoadingSpinner className="spinner" />
                                <span>Waiting for player 2 to join...</span>
                            </div>
                            {message && <div className="alert info">{message}</div>}

                        </div>
                    )}
                </div>


            </main>

            <footer className="footer">
                <p className="copyright">
                    Â© {new Date().getFullYear()} Number Position Game. All rights reserved.
                </p>
                <div className="footer-links">
                    <Link to="/privacy">Privacy Policy</Link>
                    <Link to="/terms">Terms of Service</Link>
                </div>
                <PlayerJoinPoll gameId={gameId} playerName={playerName} navigate={navigate} />
            </footer>
        </div>
    );
};
;
`;

const joinGameCode = `// src/components/JoinGame.js
import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './JoinGame.css';

const JoinGame = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [playerName, setPlayerName] = useState(location.state?.playerName || '');
    const [gameId, setGameId] = useState('');
    const [message, setMessage] = useState('');
    const [isJoining, setIsJoining] = useState(false);

    useEffect(() => {
        if (!playerName) {
            navigate('/');
        }
    }, [playerName, navigate]);

    const handleJoinGame = async () => {
        if (!gameId) {
            alert('Please enter the game code');
            return;
        }
        setIsJoining(true);
    };

    return (
        <div className="join-game-container">
            <nav className="nav-bar">
                <h1 className="logo">NUMBER<span className="logo-accent">POSITION</span></h1>
            </nav>

            <main className="main-content">
                <div className="game-card">
                    <div className="join-panel">
                        <h2>Join Existing Game</h2>
                        <div className="player-info">
                            <span className="player-label">Player Name:</span>
                            <span className="player-name">{playerName}</span>
                        </div>

                        <div className="input-group">
                            <input
                                type="text"
                                placeholder="Enter Game Code"
                                value={gameId}
                                onChange={(e) => setGameId(e.target.value)}
                                className="code-input"
                            />
                        </div>

                        <button
                            className="btn join-btn"
                            onClick={handleJoinGame}
                            disabled={isJoining}
                        >
                            {isJoining ? 'Joining...' : 'Join Game'}
                        </button>

                        
                    </div>
                </div>
            </main>

            <footer className="footer">
                <p className="copyright">
                    Â© {new Date().getFullYear()} Number Position Game. All rights reserved.
                </p>
            </footer>
        </div>
    );
};

export default JoinGame; `;

const gameplayCode = `// src/components/GamePageCreator.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import NumberHelper from './NumberHelper';
import { ReactComponent as ConfettiIcon } from '../assets/confetti.svg';
import './GamePageCreator.css';

const GamePageCreator = () => {
    const { gameId } = useParams();
    const navigate = useNavigate();
    const [gameData, setGameData] = useState(null);
    const [guess, setGuess] = useState('');
    const [guessMessage, setGuessMessage] = useState('');
    const [turnTrigger, setTurnTrigger] = useState(null);
    const [gameEnded, setGameEnded] = useState(false);

    // Poll game data every 3 seconds
    useEffect(() => {
        const interval = setInterval(async () => {

// For the creator, assume playerName is the first element.
const playerName = gameData.players[0];
const myGuesses = (gameData.guesses ?? []).filter(g => g.player === playerName);
const oppGuesses = (gameData.guesses ?? []).filter(g => g.player === gameData.players[1]);
const myTurn = gameData.turn === playerName;
const finished = gameData.status === 'finished';

const handleSubmitGuess = async (e) => {
    e.preventDefault();
    if (!guess) {
        alert('Please enter a guess.');
        return;
    }
    if (guess.length !== 4 || new Set(guess).size !== 4) {
        setGuessMessage('Please enter exactly 4 unique digits.');
        return;
    }
    try {
        const res = await axios.post('http://localhost:5000/api/games/guess', { gameId, player: playerName, guess });
        setGuessMessage(res.data.message);
        setGuess('');
    } catch (err) {
        console.error('Error submitting guess:', err);
        setGuessMessage(err.response?.data?.message || 'Error submitting guess');
    }
};

// Quit Game: call the quitGame endpoint to end the game
const handleQuitGame = async () => {
    const confirmation = window.confirm('Are you sure you want to end the game? You will be counted as the loser.');
    if (confirmation) {
        try {
            await axios.post('http://localhost:5000/api/games/quitGame', { gameId, player: playerName });
            setGameEnded(true);
        } catch (err) {
            console.error('Error ending the game:', err);
        }
    }
};

let outcomeMessage = '';
if (finished) {
    if (gameData.winner === "draw") {
        outcomeMessage = "It's a draw!";
    } else if (gameData.winner === playerName) {
        outcomeMessage = "Congratulations! You win! Confetti!!!";
    } else {
        outcomeMessage = "You lost! Better luck next time!";
    }
}

const handleResetGame = async () => {
    try {
        await axios.post('http://localhost:5000/api/games/reset', { gameId });
        navigate('/');
    } catch (err) {
        console.error('Error resetting game:', err);
    }
};

return (
    <div className="game-container creator-view">
        <nav className="nav-bar">
            <h1 className="logo">
                NUMBER<span className="logo-accent">POSITION</span>
            </h1>
            {!finished && (
                <button onClick={handleQuitGame} className="btn quit-game-btn">
                    Quit Game
                </button>
            )}
        </nav>

        <main className="game-main">
            <div className="game-header">
                <h2 className="game-title">
                    Game Room: <span className="code">{gameId}</span>
                </h2>
                <div className="status-badge">
                    {finished ? 'Game Over' : myTurn ? 'Your Turn' : "Opponent's Turn"}
                </div>
            </div>

            <div className="secret-code-display">
                Your Secret Code: <span className="code">{gameData.secretCode1}</span>
            </div>

            {finished && (
                <>
                    <div className="outcome-banner">
                        {gameData.winner === "draw" ? (
                            <><span>ð¤</span> {outcomeMessage}</>
                        ) : gameData.winner === playerName ? (
                            <><ConfettiIcon className="confetti" /> {outcomeMessage}</>
                        ) : (
                            <><span>ð</span> {outcomeMessage}</>
                        )}
                        <button className="btn home-btn" onClick={() => navigate('/')}>
                            Return to Home
                        </button>
                    </div>
                    {/* Reveal both secret codes once the game is finished */}
                    <div className="secret-code-reveal">
                        <h3>Secret Codes Revealed</h3>
                        <p>
                            {gameData.players[0]}'s Secret Code: <span className="code">{gameData.secretCode1}</span>
                        </p>
                        <p>
                            {gameData.players[1]}'s Secret Code: <span className="code">{gameData.secretCode2}</span>
                        </p>
                    </div>
                </>
            )}

            {!finished && !gameEnded && (
                <>
                    <div className="guess-history-container">
                        <div className="guess-column">
                            <h3 className="column-title">Your Guesses</h3>
                            <div className="guess-table">
                                <div className="table-header">
                                    <div>Guess</div>
                                    <div>Numbers Correct</div>
                                    <div>Positions Correct</div>
                                </div>
                                {myGuesses.length === 0 ? (
                                    <div className="empty-state">No guesses yet</div>
                                ) : (
                                    myGuesses.map((g, index) => (
                                        <div className="table-row" key={index}>
                                            <div>{g.guess}</div>
                                            <div className="correct-count">{g.numbersCorrect}</div>
                                            <div className="correct-count">{g.positionsCorrect}</div>
                                        </div>
                                    ))
                                )}
                            </div>
                        </div>

                        <div className="guess-column">
                            <h3 className="column-title">Opponent's Guesses</h3>
                            <div className="guess-table">
                                <div className="table-header">
                                    <div>Guess</div>
                                    <div>Numbers Correct</div>
                                    <div>Positions Correct</div>
                                </div>
                                {oppGuesses.length === 0 ? (
                                    <div className="empty-state">No guesses yet</div>
                                ) : (
                                    oppGuesses.map((g, index) => (
                                        <div className="table-row" key={index}>
                                            <div>{g.guess}</div>
                                            <div className="correct-count">{g.numbersCorrect}</div>
                                            <div className="correct-count">{g.positionsCorrect}</div>
                                        </div>
                                    ))
                                )}
                            </div>
                        </div>
                    </div>

                    <div className="guess-input-section">
                        {myTurn ? (
                            <form className="guess-form" onSubmit={handleSubmitGuess}>
                                <input
                                    type="text"
                                    className="guess-input"
                                    placeholder="Enter your guess"
                                    value={guess}
                                    onChange={(e) => setGuess(e.target.value)}
                                    maxLength="4"
                                    pattern="\d{4}"
                                />
                                <button
                                    type="submit"
                                    className="btn submit-guess-btn"
                                    disabled={!guess || guess.length !== 4 || new Set(guess).size !== 4}
                                >
                                    Submit Guess
                                </button>
                            </form>
                        ) : (
                            <p className="waiting-turn">
                                <span className="pulse-dot"></span>
                                Waiting for your turn...
                            </p>
                        )}
                        <NumberHelper />
                        {guessMessage && <div className="guess-feedback">{guessMessage}</div>}
                    </div>
                </>
            )}
        </main>
    </div>
);
};

export default GamePageCreator;
`;
























