import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const GamePage = () => {
    const { gameId } = useParams();
    const [gameData, setGameData] = useState(null);
    const [error, setError] = useState(null);
    const [guess, setGuess] = useState('');
    const [playerTurn, setPlayerTurn] = useState('player1');  // Track current player turn

    useEffect(() => {
        console.log('Game ID from URL:', gameId);
        axios.get(`/api/games/${gameId}`)
            .then(response => {
                setGameData(response.data);
            })
            .catch(err => {
                console.error('Error fetching game data:', err.response || err);
                setError('Error fetching game data');
            });
    }, [gameId]);

    const handleSubmitGuess = () => {
        if (guess.length !== 4 || new Set(guess).size !== 4) {
            alert('Guess must be 4 unique digits.');
            return;
        }
        axios.post(`/api/games/guess`, {
            gameId, player: playerTurn, guess
        })
            .then(() => {
                alert('Guess submitted successfully!');
                setPlayerTurn(playerTurn === 'player1' ? 'player2' : 'player1');
                setGuess('');
            })
            .catch(err => alert('Error submitting guess: ' + err.response.data.message));
    };

    if (error) {
        return <div>{error}</div>;
    }

    if (!gameData) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Game: {gameData.gameId}</h1>
            <p>Players: {gameData.players.join(', ')}</p>
            <p>Status: {gameData.status}</p>
            <p>Guesses: {gameData.guesses.length}</p>
            <input
                type="text"
                value={guess}
                onChange={(e) => setGuess(e.target.value)}
                maxLength={4}
                placeholder="Enter 4-digit guess"
            />
            <button onClick={handleSubmitGuess}>Submit Guess</button>
        </div>
    );
};

export default GamePage;
