// src/components/Home.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import githubIcon from '../assets/github-icon.png';

const Home = () => {
    const [playerName, setPlayerName] = useState('');
    const navigate = useNavigate();

    const handleCreate = () => {
        if (!playerName) {
            alert('Please enter your name');
            return;
        }
        navigate('/create-game', { state: { playerName } });
    };

    const handleJoin = () => {
        if (!playerName) {
            alert('Please enter your name');
            return;
        }
        navigate('/join-game', { state: { playerName } });
    };

    return (
        <div className="home-container">
            {/* Navigation Bar */}
            <nav className="nav-bar">
                <h1 className="logo">NUMBER<span className="logo-accent">POSITION</span></h1>
                <div className="nav-links">
                    <a href="/about">About</a>
                    <a href="https://github.com/yourusername/number-position-game" target="_blank" rel="noopener noreferrer">
                        <img
                            src={githubIcon}
                            alt="GitHub Repository"
                            className="github-icon"
                        />
                    </a>
                </div>
            </nav>

            {/* Main Content */}
            <main className="main-content">
                <div className="game-card">
                    <h2 className="title">Start Playing</h2>
                    <div className="input-group">
                        <input
                            type="text"
                            placeholder="Enter your name"
                            value={playerName}
                            onChange={e => setPlayerName(e.target.value)}
                            className="name-input"
                        />
                    </div>
                    <div className="button-group">
                        <button onClick={handleCreate} className="btn create-btn">
                            Create New Game
                        </button>
                        <button onClick={handleJoin} className="btn join-btn">
                            Join Existing Game
                        </button>
                    </div>
                </div>
            </main>

            {/* Footer */}
            <footer className="footer">
                <p className="copyright">
                    Â© {new Date().getFullYear()} Number Position Game. All rights reserved.
                </p>
                <div className="footer-links">
                    <a href="/privacy">Privacy Policy</a>
                    <a href="/terms">Terms of Service</a>
                </div>
            </footer>
        </div>
    );
};

export default Home;
