import React, { useState } from 'react';

function App() {
    const [guess, setGuess] = useState('');
    const [correctCount, setCorrectCount] = useState(0);

    const secretCode = '9430';  // Hardcoded for now, could be dynamic

    // Function to handle guess submission
    const handleSubmit = () => {
        let correct = 0;
        let misplaced = 0;

        for (let i = 0; i < secretCode.length; i++) {
            if (guess[i] === secretCode[i]) {
                correct++;
            } else if (secretCode.includes(guess[i])) {
                misplaced++;
            }
        }

        setCorrectCount({ correct, misplaced });
    };

    return (
        <div>
            <h1>Number Position Game</h1>
            <p>Guess the secret 4-digit number!</p>
            <input
                type="text"
                value={guess}
                onChange={(e) => setGuess(e.target.value)}
                placeholder="Enter your guess"
            />
            <button onClick={handleSubmit}>Submit Guess</button>
            <div>
                {correctCount > 0 && (
                    <p>
                        Correct: {correctCount.correct}, Misplaced: {correctCount.misplaced}
                    </p>
                )}
            </div>
        </div>
    );
}

export default App;
