// src/components/GamePageJoiner.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const GamePageJoiner = () => {
    const { gameId } = useParams();
    const [gameData, setGameData] = useState(null);

    // Poll for game data every 3 seconds
    useEffect(() => {
        const interval = setInterval(async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/games/${gameId}`);
                setGameData(res.data);
            } catch (err) {
                console.error('Error fetching game data:', err);
            }
        }, 3000);
        return () => clearInterval(interval);
    }, [gameId]);

    if (!gameData) return <p>Loading game data...</p>;

    // Use a fallback empty array in case gameData.guesses is undefined.
    const player2Guesses = (gameData.guesses ?? []).filter(g => g.player === gameData.players[1]);

    return (
        <div>
            <h2>Player 2 Game Page</h2>
            <p>Your Secret Code: {gameData.secretCode2}</p>
            <h3>Your Guess History</h3>
            {player2Guesses.length === 0 ? (
                <p>No guesses yet.</p>
            ) : (
                <table border="1">
                    <thead>
                        <tr>
                            <th>Guess</th>
                            <th>Numbers Correct</th>
                            <th>Positions Correct</th>
                        </tr>
                    </thead>
                    <tbody>
                        {player2Guesses.map((guess, index) => (
                            <tr key={index}>
                                <td>{guess.guess}</td>
                                <td>{guess.numbersCorrect}</td>
                                <td>{guess.positionsCorrect}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
            {/* Add input and submission for new guesses here if needed */}
        </div>
    );
};

export default GamePageJoiner;
