// src/components/JoinGame.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const JoinGame = () => {
    const navigate = useNavigate();
    const [playerName, setPlayerName] = useState('');
    const [gameId, setGameId] = useState('');
    const [message, setMessage] = useState('');

    const handleJoinGame = async () => {
        if (!playerName || !gameId) {
            alert('Please enter both your name and the game code.');
            return;
        }
        try {
            const response = await axios.post('http://localhost:5000/api/games/join', { gameId, playerName });
            const game = response.data.game;
            setMessage(response.data.message);
            if (game.players.length === 2 && game.status === 'waitingForSecret') {
                // Redirect to the secret code page using playerName and gameId as URL parameters
                navigate(`/${playerName}/setsecretcode/${gameId}`);
            }
        } catch (err) {
            console.error('Error joining game:', err);
            setMessage('Error joining game');
        }
    };

    return (
        <div>
            <h2>Join Game</h2>
            <input
                type="text"
                placeholder="Enter your name"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
            />
            <input
                type="text"
                placeholder="Enter game code"
                value={gameId}
                onChange={(e) => setGameId(e.target.value)}
            />
            <button onClick={handleJoinGame}>Join Game</button>
            {message && <p>{message}</p>}
        </div>
    );
};

export default JoinGame;
