// src/components/Timer.js
import React, { useState, useEffect } from 'react';
import './Timer.css'; // Optional: create this file to style the timer

const Timer = ({ initialTime, onTimeUp, resetTrigger }) => {
    const [timeRemaining, setTimeRemaining] = useState(initialTime);

    // Reset timer when resetTrigger changes or when initialTime changes
    useEffect(() => {
        setTimeRemaining(initialTime);
    }, [resetTrigger, initialTime]);

    // Countdown logic: decrease the timer every second
    useEffect(() => {
        if (timeRemaining <= 0) {
            // When time runs out, call the onTimeUp callback
            onTimeUp();
            return;
        }
        const interval = setInterval(() => {
            setTimeRemaining(prev => prev - 1);
        }, 1000);
        return () => clearInterval(interval);
    }, [timeRemaining, onTimeUp]);

    // Format the seconds into mm:ss format
    const formatTime = (seconds) => {
        const min = Math.floor(seconds / 60);
        const sec = seconds % 60;
        return `${min}:${sec < 10 ? '0' : ''}${sec}`;
    };

    return (
        <div className="timer">
            Time left: {formatTime(timeRemaining)}
        </div>
    );
};

export default Timer;
