// src/components/GamePageCreator.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import NumberHelper from './NumberHelper';
import './GamePageCreator.css';

const GamePageCreator = () => {
    const { gameId } = useParams();
    const navigate = useNavigate();
    const [gameData, setGameData] = useState(null);
    const [guess, setGuess] = useState('');
    const [guessMessage, setGuessMessage] = useState('');
    const [turnTrigger, setTurnTrigger] = useState(null);
    const [gameEnded, setGameEnded] = useState(false);

    // Poll game data every 3 seconds
    useEffect(() => {
        const interval = setInterval(async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/games/${gameId}`);
                setGameData(res.data);
                setTurnTrigger(res.data.turn);
            } catch (err) {
                console.error('Error fetching game data:', err);
            }
        }, 3000);
        return () => clearInterval(interval);
    }, [gameId]);

    if (!gameData) return <p>Loading game data...</p>;

    // For creator, playerName is the first element.
    const playerName = gameData.players[0];
    const myGuesses = (gameData.guesses ?? []).filter(g => g.player === playerName);
    const oppGuesses = (gameData.guesses ?? []).filter(g => g.player === gameData.players[1]);
    const myTurn = gameData.turn === playerName;
    const finished = gameData.status === 'finished';

    const handleSubmitGuess = async (e) => {
        e.preventDefault();
        if (!guess) {
            alert('Please enter a guess.');
            return;
        }
        if (guess.length !== 4 || new Set(guess).size !== 4) {
            setGuessMessage('Please enter exactly 4 unique digits.');
            return;
        }
        try {
            const res = await axios.post('http://localhost:5000/api/games/guess', { gameId, player: playerName, guess });
            setGuessMessage(res.data.message);
            setGuess('');
        } catch (err) {
            console.error('Error submitting guess:', err);
            setGuessMessage(err.response?.data?.message || 'Error submitting guess');
        }
    };

    // Quit game: show a confirmation alert, then actually call endpoints to end the game.
    const handleQuitGame = async () => {
        const confirmation = window.confirm('Are you sure you want to end the game? You will be counted as the loser.');
        if (confirmation) {
            try {
                await axios.post('http://localhost:5000/api/games/end', { gameId, player: playerName });
                const opponent = gameData.players[0] === playerName ? gameData.players[1] : gameData.players[0];
                await axios.post('http://localhost:5000/api/games/endGame', { gameId, winner: opponent, loser: playerName });
                setGameEnded(true);
            } catch (err) {
                console.error('Error ending the game:', err);
            }
        }
    };

    let outcomeMessage = '';
    if (finished) {
        if (gameData.winner === "draw") {
            outcomeMessage = "It's a draw!";
        } else if (gameData.winner === playerName) {
            outcomeMessage = "Congratulations! You win! Confetti!!!";
        } else {
            outcomeMessage = "You lost! Better luck next time!";
        }
    }

    const handleResetGame = async () => {
        try {
            await axios.post('http://localhost:5000/api/games/reset', { gameId });
            navigate('/');
        } catch (err) {
            console.error('Error resetting game:', err);
        }
    };

    return (
        <div>
            <div className="top-bar">
                <h2>Player 1 Game Page</h2>
                {!finished && (
                    <button onClick={handleQuitGame} className="btn quit-game-btn">
                        Quit Game
                    </button>
                )}
            </div>
            <p>Your Secret Code: {gameData.secretCode1}</p>
            <p><strong>Turn:</strong> {gameData.turn ? gameData.turn : 'Not set'}</p>
            {finished && (
                <div className="outcome-banner">
                    <h3>Game Over</h3>
                    <p>{outcomeMessage}</p>
                    <button onClick={() => navigate('/')}>Go to Home Page</button>
                    <button onClick={handleResetGame}>Restart Game</button>
                </div>
            )}
            {!finished && (
                <>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <div style={{ width: '48%' }}>
                            <h3>Your Guess History</h3>
                            {myGuesses.length === 0 ? (
                                <p>No guesses yet.</p>
                            ) : (
                                <table border="1">
                                    <thead>
                                        <tr>
                                            <th>Guess</th>
                                            <th>Numbers Correct</th>
                                            <th>Positions Correct</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {myGuesses.map((g, index) => (
                                            <tr key={index}>
                                                <td>{g.guess}</td>
                                                <td>{g.numbersCorrect}</td>
                                                <td>{g.positionsCorrect}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            )}
                        </div>
                        <div style={{ width: '48%' }}>
                            <h3>Opponent's Guess History</h3>
                            {oppGuesses.length === 0 ? (
                                <p>No guesses yet.</p>
                            ) : (
                                <table border="1">
                                    <thead>
                                        <tr>
                                            <th>Guess</th>
                                            <th>Numbers Correct</th>
                                            <th>Positions Correct</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {oppGuesses.map((g, index) => (
                                            <tr key={index}>
                                                <td>{g.guess}</td>
                                                <td>{g.numbersCorrect}</td>
                                                <td>{g.positionsCorrect}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            )}
                        </div>
                    </div>
                    {myTurn ? (
                        <form onSubmit={handleSubmitGuess}>
                            <input
                                type="text"
                                placeholder="Enter your guess"
                                value={guess}
                                onChange={(e) => setGuess(e.target.value)}
                                maxLength="4"
                                pattern="\d{4}"
                            />
                            <button type="submit" className="btn submit-guess-btn"
                                disabled={!guess || guess.length !== 4 || new Set(guess).size !== 4}
                            >
                                Submit Guess
                            </button>
                        </form>
                    ) : (
                        <p>Waiting for your turn...</p>
                    )}
                    <NumberHelper />
                    {guessMessage && <div className="guess-feedback">{guessMessage}</div>}
                </>
            )}
        </div>
    );
};

export default GamePageCreator;
