// src/components/Timer.js
import React, { useState, useEffect } from 'react';
import './Timer.css'; // Create styling as needed

const Timer = ({ initialTime, onTimeUp, resetTrigger }) => {
    const [timeRemaining, setTimeRemaining] = useState(initialTime);
    const [timeUpCalled, setTimeUpCalled] = useState(false);

    // Reset timer and flag when resetTrigger or initialTime changes
    useEffect(() => {
        setTimeRemaining(initialTime);
        setTimeUpCalled(false);
    }, [resetTrigger, initialTime]);

    useEffect(() => {
        if (timeRemaining <= 0 && !timeUpCalled) {
            setTimeUpCalled(true);
            onTimeUp();
            return;
        }
        const interval = setInterval(() => {
            setTimeRemaining(prev => prev - 1);
        }, 1000);
        return () => clearInterval(interval);
    }, [timeRemaining, onTimeUp, timeUpCalled]);

    const formatTime = (seconds) => {
        const min = Math.floor(seconds / 60);
        const sec = seconds % 60;
        return `${min}:${sec < 10 ? '0' : ''}${sec}`;
    };

    return (
        <div className="timer">
            Time left: {formatTime(timeRemaining)}
        </div>
    );
};

export default Timer;
