import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './CreateGame.css';

const CreateGame = () => {
    const { state } = useLocation();
    const [gameId, setGameId] = useState(state ? state.gameId : '');
    const [playerName, setPlayerName] = useState(state ? state.playerName : '');
    const [playerJoined, setPlayerJoined] = useState(false);  // Tracks real player join
    const navigate = useNavigate();

    useEffect(() => {
        if (!state) {
            alert('No player information found!');
            navigate('/');
        }
    }, [state, navigate]);

    const handlePlayerJoin = () => {
        // Simulate a backend-triggered event to set this when another player joins
        setPlayerJoined(true);  // Use this only when backend confirms player joining
    };

    return (
        <div className="game-container">
            <h1>Game ID: {gameId}</h1>
            <p>Welcome, {playerName}!</p>
            {!playerJoined ? (
                <p>Waiting for another player to join...</p>
            ) : (
                <p>Another player has joined! Starting game...</p>
            )}
            {/* Future button or backend trigger to simulate join */}
            <button className="button" onClick={handlePlayerJoin}>
                Simulate Player Join (For Testing)
            </button>
        </div>
    );
};

export default CreateGame;
