import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './CreateGame.css';

const CreateGame = () => {
    const { state } = useLocation();
    const [gameName, setGameName] = useState(state ? state.gameName : '');
    const [playerName, setPlayerName] = useState(state ? state.playerName : '');
    const navigate = useNavigate();

    useEffect(() => {
        if (!state) {
            alert('No game or player information found!');
            navigate('/');
        }
    }, [state, navigate]);

    const handleStartGame = () => {
        if (!gameName || !playerName) {
            alert('Please fill in both fields!');
            return;
        }
        // Logic for creating the game can go here
        alert(`Game "${gameName}" started by ${playerName}`);
        navigate(`/game/${gameName}`);
    };

    return (
        <div className="game-container">
            <h1>Game Setup</h1>
            <p>You're about to start the game "{gameName}" with player "{playerName}".</p>

            <div className="form-container">
                <button className="button" onClick={handleStartGame}>
                    Start Game
                </button>
            </div>
        </div>
    );
};

export default CreateGame;
