import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import NumberHelper from './NumberHelper';
import Timer from './Timer';
import './GamePageJoiner.css';
import { ReactComponent as ConfettiIcon } from '../assets/confetti.svg';

const GamePage = () => {
    const { gameId } = useParams();
    const navigate = useNavigate();
    const [gameData, setGameData] = useState(null);
    const [guess, setGuess] = useState('');
    const [guessMessage, setGuessMessage] = useState('');
    const [turnTrigger, setTurnTrigger] = useState(null);
    const [showQuitConfirmation, setShowQuitConfirmation] = useState(false);
    const [gameEnded, setGameEnded] = useState(false);

    useEffect(() => {
        const interval = setInterval(async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/games/${gameId}`);
                setGameData(res.data);
                setTurnTrigger(res.data.turn);
            } catch (err) {
                console.error('Error fetching game data:', err);
            }
        }, 3000);
        return () => clearInterval(interval);
    }, [gameId]);

    if (!gameData) return <p>Loading game data...</p>;

    const playerName = gameData.players[0]; // Player 1 is the creator, Player 2 is the joiner
    const isCreator = gameData.players[0] === playerName;
    const myGuesses = (gameData.guesses ?? []).filter(g => g.player === playerName);
    const oppGuesses = (gameData.guesses ?? []).filter(g => g.player !== playerName);
    const myTurn = gameData.turn === playerName;
    const finished = gameData.status === 'finished';

    const handleSubmitGuess = async (e) => {
        e.preventDefault();
        if (guess.length !== 4 || new Set(guess).size !== 4) {
            setGuessMessage('Please enter exactly 4 unique digits.');
            return;
        }
        try {
            const res = await axios.post('http://localhost:5000/api/games/guess', { gameId, player: playerName, guess });
            setGuessMessage(res.data.message);
            setGuess('');
        } catch (err) {
            console.error('Error submitting guess:', err);
            setGuessMessage(err.response?.data?.message || 'Error submitting guess');
        }
    };

    const onTimeUp = async () => {
        if (myTurn && gameData.status !== 'finished') {
            try {
                await axios.post('http://localhost:5000/api/games/guess', { gameId, player: playerName, guess: '' });
                setGuessMessage('Time is up! Turn skipped.');
                await axios.post('http://localhost:5000/api/games/nextTurn', { gameId });
            } catch (err) {
                console.error('Error auto-submitting guess:', err);
            }
        }
    };

    const handleQuitGame = async () => {
        const confirmation = window.confirm('Are you sure you want to end the game? You will be counted as the loser.');
        if (confirmation) {
            try {
                await axios.post('http://localhost:5000/api/games/end', { gameId, player: playerName });
                const winner = gameData.players[0] === playerName ? gameData.players[1] : gameData.players[0];
                await axios.post('http://localhost:5000/api/games/endGame', { gameId, winner, loser: playerName });
                setGameEnded(true);
            } catch (err) {
                console.error('Error ending the game:', err);
            }
        }
    };

    let outcomeMessage = '';
    if (finished) {
        outcomeMessage = gameData.winner === "draw" ? "It's a draw!" :
            gameData.winner === playerName ? "Congratulations! You win!" :
                "You lost! Better luck next time!";
    }

    return (
        <div className="game-container">
            <nav className="nav-bar">
                <h1 className="logo">
                    NUMBER<span className="logo-accent">POSITION</span>
                </h1>
                <button onClick={handleQuitGame} className="btn quit-game-btn">Quit Game</button>
            </nav>

            <main className="game-main">
                <div className="game-header">
                    <h2 className="game-title">
                        Game Room: <span className="code">{gameId}</span>
                    </h2>
                    <div className="status-badge">
                        {finished ? 'Game Over' : myTurn ? 'Your Turn' : "Opponent's Turn"}
                    </div>
                </div>

                {finished && (
                    <div className="outcome-banner">
                        {gameData.winner === "draw" ? (
                            <><span>🤝</span> {outcomeMessage}</>
                        ) : gameData.winner === playerName ? (
                            <><ConfettiIcon className="confetti" /> {outcomeMessage}</>
                        ) : (
                            <><span>💔</span> {outcomeMessage}</>
                        )}
                        <button className="btn home-btn" onClick={() => navigate('/')}>
                            Return to Home
                        </button>
                    </div>
                )}

                <div className="secret-code-display">
                    Your Secret Code: <span className="code">{isCreator ? gameData.secretCode1 : gameData.secretCode2}</span>
                </div>

                {!finished && <Timer initialTime={120} onTimeUp={onTimeUp} resetTrigger={turnTrigger} />}

                <div className="guess-history-container">
                    <div className="guess-column">
                        <h3 className="column-title">Your Guesses</h3>
                        <div className="guess-table">
                            <div className="table-header">
                                <div>Guess</div>
                                <div>Numbers</div>
                                <div>Position</div>
                            </div>
                            {myGuesses.map((g, index) => (
                                <div className="table-row" key={index}>
                                    <div>{g.guess}</div>
                                    <div className="correct-count">{g.numbersCorrect}</div>
                                    <div className="correct-count">{g.positionsCorrect}</div>
                                </div>
                            ))}
                            {myGuesses.length === 0 && <div className="empty-state">No guesses yet</div>}
                        </div>
                    </div>

                    <div className="guess-column">
                        <h3 className="column-title">Opponent's Guesses</h3>
                        <div className="guess-table">
                            <div className="table-header">
                                <div>Guess</div>
                                <div>Numbers</div>
                                <div>Position</div>
                            </div>
                            {oppGuesses.map((g, index) => (
                                <div className="table-row" key={index}>
                                    <div>{g.guess}</div>
                                    <div className="correct-count">{g.numbersCorrect}</div>
                                    <div className="correct-count">{g.positionsCorrect}</div>
                                </div>
                            ))}
                            {oppGuesses.length === 0 && <div className="empty-state">No guesses yet</div>}
                        </div>
                    </div>
                </div>

                {!finished && !gameEnded && (
                    <div className="guess-input-section">
                        {myTurn ? (
                            <form className="guess-form" onSubmit={handleSubmitGuess}>
                                <input
                                    type="text"
                                    className="guess-input"
                                    placeholder="Enter 4 unique digits"
                                    value={guess}
                                    onChange={(e) => {
                                        const value = e.target.value.replace(/\D/g, '').slice(0, 4);
                                        setGuess(value);
                                    }}
                                    maxLength="4"
                                    pattern="\d{4}"
                                />
                                <button
                                    type="submit"
                                    className="btn submit-guess-btn"
                                    disabled={!guess || guess.length !== 4 || new Set(guess).size !== 4}
                                >
                                    Submit Guess
                                </button>
                            </form>
                        ) : (
                            <div className="waiting-turn">
                                <div className="pulse-dot"></div>
                                Waiting for opponent's move...
                            </div>
                        )}
                        <NumberHelper />
                        {guessMessage && <div className="guess-feedback">{guessMessage}</div>}
                    </div>
                )}
            </main>
        </div>
    );
};

export default GamePage;
