import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const GamePage = () => {
    const { gameId } = useParams();  // Extract the gameId from the URL
    const [gameData, setGameData] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Log the gameId to check if it's correct
        console.log('Game ID from URL:', gameId);

        // Fetch the game data from the API
        axios.get(`/api/games/${gameId}`)
            .then(response => {
                setGameData(response.data);  // Store the game data
            })
            .catch(err => {
                console.error('Error fetching game data:', err.response || err);  // Log the full error
                setError('Error fetching game data');
            });
    }, [gameId]);  // Re-run the effect when the gameId changes

    // Show an error message if there's an issue
    if (error) {
        return <div>{error}</div>;
    }

    // Show a loading message if the game data hasn't been fetched yet
    if (!gameData) {
        return <div>Loading...</div>;
    }

    // Render the game data once it's available
    return (
        <div>
            <h1>Game: {gameData.gameId}</h1>
            <p>Players: {gameData.players.join(', ')}</p>
            <p>Status: {gameData.status}</p>
            <p>Guesses: {gameData.guesses.length}</p>
        </div>
    );
};

export default GamePage;
