// src/components/GamePageCreator.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const GamePageCreator = () => {
    const { gameId } = useParams();
    const [gameData, setGameData] = useState(null);

    // Poll for game data every 3 seconds
    useEffect(() => {
        const interval = setInterval(async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/games/${gameId}`);
                setGameData(res.data);
            } catch (err) {
                console.error('Error fetching game data:', err);
            }
        }, 3000);
        return () => clearInterval(interval);
    }, [gameId]);

    if (!gameData) return <p>Loading game data...</p>;

    return (
        <div>
            <h2>Player 1 Game Page</h2>
            <p>Your Secret Code: {gameData.secretCode1}</p>
            <h3>Your Guess History</h3>
            <table border="1">
                <thead>
                    <tr>
                        <th>Guess</th>
                        <th>Numbers Correct</th>
                        <th>Positions Correct</th>
                    </tr>
                </thead>
                <tbody>
                    {gameData.guesses
                        .filter(g => g.player === gameData.players[0])
                        .map((guess, index) => (
                            <tr key={index}>
                                <td>{guess.guess}</td>
                                <td>{guess.numbersCorrect}</td>
                                <td>{guess.positionsCorrect}</td>
                            </tr>
                        ))}
                </tbody>
            </table>
            {/* You can add an input here for Player 1 to submit a new guess */}
        </div>
    );
};

export default GamePageCreator;
