import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css'; // Assuming this CSS file exists and is linked properly

const Home = () => {
    const [gameName, setGameName] = useState('');
    const [playerName, setPlayerName] = useState('');
    const navigate = useNavigate();

    const handleCreateGame = () => {
        if (!gameName || !playerName) {
            alert('Please fill in both fields!');
            return;
        }
        navigate(`/game/${gameName}`);
    };

    const handleJoinGame = () => {
        if (!gameName || !playerName) {
            alert('Please fill in both fields!');
            return;
        }
        navigate(`/game/${gameName}`);
    };

    return (
        <div className="home-container">
            <div className="header-container">
                <h1 className="title">Welcome to the Game!</h1>
                <p className="subtitle">Start a new game or join an existing one.</p>
            </div>

            <div className="form-container">
                <input
                    className="input-field"
                    type="text"
                    placeholder="Enter your name"
                    value={playerName}
                    onChange={(e) => setPlayerName(e.target.value)}
                />
                <input
                    className="input-field"
                    type="text"
                    placeholder="Enter game name"
                    value={gameName}
                    onChange={(e) => setGameName(e.target.value)}
                />
                <div className="button-container">
                    <button className="button" onClick={handleCreateGame}>
                        Start New Game
                    </button>
                    <button className="button" onClick={handleJoinGame}>
                        Join Existing Game
                    </button>
                </div>
            </div>

            <footer className="footer">
                <p>Powered by YourGame Inc. - All rights reserved</p>
            </footer>
        </div>
    );
};

export default Home;
