import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './CreateGame.css';

const CreateGame = () => {
    const { state } = useLocation();
    const [playerName, setPlayerName] = useState(state ? state.playerName : '');
    const [gameId, setGameId] = useState('');
    const [playerJoined, setPlayerJoined] = useState(false);
    const [gameStatus, setGameStatus] = useState('waiting');
    const [secretNumber, setSecretNumber] = useState('');
    const [isSecretNumberSet, setIsSecretNumberSet] = useState(false);  // Track if secret number is set
    const navigate = useNavigate();

    useEffect(() => {
        if (!state) {
            alert('No player information found!');
            navigate('/');
        }
    }, [state, navigate]);

    useEffect(() => {
        if (playerName && !gameId) {
            axios
                .post('http://localhost:5000/api/games', { players: [playerName] })
                .then(response => {
                    setGameId(response.data.gameId); // Corrected access to response data
                })
                .catch(error => {
                    console.error('Error starting the game:', error);
                });
        }
    }, [playerName, gameId]);

    useEffect(() => {
        if (playerJoined && gameId) {
            // Once both players join, navigate to the game page
            navigate(`/game/${gameId}`);
        }
    }, [playerJoined, gameId, navigate]);

    const handlePlayerJoin = () => {
        console.log('Player name:', playerName); // Add this log
        setPlayerJoined(true);
        setGameStatus('in-progress'); // Change status when second player joins
    };

    navigate('/create-game', { state: { playerName: 'Player1' } });



    useEffect(() => {
        if (gameId) {
            // Fetch game data after creation
            axios
                .get(`http://localhost:5000/api/games/${gameId}`)
                .then(response => {
                    setGameStatus(response.data.status); // Update game status from server
                })
                .catch(error => {
                    console.error('Error fetching game data:', error);
                });
        }
    }, [gameId]);

    const handleSecretNumberChange = (e) => {
        const value = e.target.value;
        if (value.length <= 4 && /^[0-9]*$/.test(value) && new Set(value).size === value.length) {
            setSecretNumber(value);
        }
    };

    const handleSetSecretNumber = () => {
        if (secretNumber.length === 4) {
            // Submit the secret number to the backend
            axios
                .post(`http://localhost:5000/api/games/secret`, { gameId, secretNumber })
                .then(response => {
                    setIsSecretNumberSet(true); // Mark the secret number as set
                })
                .catch(err => {
                    console.error('Error setting secret number:', err);
                });
        } else {
            alert('Please enter a valid 4-digit unique number!');
        }
    };

    useEffect(() => {
        if (gameStatus === 'setSecret') {
            // Show Player 1 the secret number setup screen
        } else if (gameStatus === 'player2SetSecret') {
            // Show Player 2 the secret number setup screen
        }
    }, [gameStatus]);

    return (
        <div className="game-container">
            <h1>Game ID: {gameId || 'Loading...'}</h1>
            <p>Welcome, {playerName}!</p>
            {gameStatus === 'waiting' ? (
                <>
                    <p>Waiting for another player to join...</p>
                    <button className="button" onClick={handlePlayerJoin}>
                        Simulate Player Join (For Testing)
                    </button>
                </>
            ) : (
                <p>Another player has joined! Starting game...</p>
            )}

            {gameStatus === 'in-progress' && !isSecretNumberSet && (
                <div>
                    <h2>Set a secret number (4 unique digits):</h2>
                    <input
                        type="text"
                        value={secretNumber}
                        onChange={handleSecretNumberChange}
                        maxLength={4}
                        placeholder="Enter secret number"
                    />
                    <button onClick={handleSetSecretNumber}>Set Secret Number</button>
                </div>
            )}
        </div>
    );
};

export default CreateGame;
