import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function HomePage() {
    const [gameCode, setGameCode] = useState('');
    const history = useHistory(); // React Router's hook to navigate between pages

    const handleCreateGame = () => {
        // Emit event to create a game on the server
        // On success, you can navigate to the game page
        history.push('/game'); // Redirect to game page after creating the game
    };

    const handleJoinGame = () => {
        if (gameCode) {
            // Emit event to join the game with the provided code
            history.push('/game'); // Redirect to game page after joining
        } else {
            alert('Please enter a game code');
        }
    };

    return (
        <div>
            <h1>Welcome to the Number Position Game</h1>
            <button onClick={handleCreateGame}>Create Game</button>
            <div>
                <h2>Or</h2>
                <input
                    type="text"
                    value={gameCode}
                    onChange={(e) => setGameCode(e.target.value)}
                    placeholder="Enter Game Code"
                />
                <button onClick={handleJoinGame}>Join Game</button>
            </div>
        </div>
    );
}

export default HomePage;
