import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CreateGame = () => {
    const [player1, setPlayer1] = useState('');
    const [player2, setPlayer2] = useState('');
    const [gameId, setGameId] = useState('');
    const [gameCreated, setGameCreated] = useState(false);
    const navigate = useNavigate();

    // Generate a game ID when player 1 starts
    const handleCreateGame = () => {
        const generatedGameId = Math.random().toString(36).substring(2, 8);
        setGameId(generatedGameId);
    };

    // Player 2 joins using the generated game ID
    const handlePlayerJoin = () => {
        if (!gameId || !player2) {
            alert('Game ID and Player 2 name are required');
            return;
        }

        // Send both players' data to the backend
        axios.post('http://localhost:5000/api/games', { gameId, players: [player1, player2] })
            .then(response => {
                setGameCreated(true);
                navigate(`/game/${gameId}`, { state: { playerName: player1 } });
            })
            .catch(error => {
                console.error('Error creating the game:', error);
            });
    };

    return (
        <div>
            <h2>Create a Game</h2>
            <input
                type="text"
                placeholder="Enter Player 1 Name"
                value={player1}
                onChange={(e) => setPlayer1(e.target.value)}
            />
            <button onClick={handleCreateGame}>Generate Game ID</button>

            {gameId && (
                <div>
                    <h3>Game ID: {gameId}</h3>
                    <input
                        type="text"
                        placeholder="Enter Player 2 Name"
                        value={player2}
                        onChange={(e) => setPlayer2(e.target.value)}
                    />
                    <button onClick={handlePlayerJoin}>Join Game</button>
                </div>
            )}
        </div>
    );
};

export default CreateGame;
