import React, { useState, useEffect } from 'react';
import { sendGuess, listenForGuessUpdates } from './services/gameService'; // For socket communication
import GuessBoard from './components/GuessBoard';

function GamePage() {
    const [secretCode, setSecretCode] = useState('');
    const [guesses, setGuesses] = useState([]);

    useEffect(() => {
        listenForGuessUpdates((result) => {
            setGuesses((prevGuesses) => [...prevGuesses, result]);
        });
    }, []);

    const handleSecretCodeSubmit = (event) => {
        event.preventDefault();
        // Emit the secret code to the server (for the other player to see)
        // In this example, we're assuming both players enter the code before the game starts
        sendGuess(secretCode); // Send secret code to the server for the other player
    };

    return (
        <div>
            <h2>Game Page</h2>
            <form onSubmit={handleSecretCodeSubmit}>
                <input
                    type="text"
                    value={secretCode}
                    onChange={(e) => setSecretCode(e.target.value)}
                    placeholder="Enter Your Secret Code"
                />
                <button type="submit">Start Game</button>
            </form>
            <GuessBoard guesses={guesses} />
        </div>
    );
}

export default GamePage;
