import React, { useState } from "react";
import "./App.css";

function App() {
    // Generate a random 4-digit number as the secret number
    const generateSecretNumber = () => {
        const number = Math.floor(Math.random() * 9000) + 1000;
        return number.toString(); // Ensure it's a string to compare digits easily
    };

    // State variables
    const [secretNumber, setSecretNumber] = useState(generateSecretNumber());
    const [guess, setGuess] = useState("");
    const [attempts, setAttempts] = useState(0);
    const [feedback, setFeedback] = useState("");

    // Handle guess submission
    const handleGuessSubmit = () => {
        if (guess.length !== 4 || isNaN(guess)) {
            setFeedback("Please enter a valid 4-digit number.");
            return;
        }

        setAttempts(attempts + 1);
        let correctDigits = 0;
        let correctPositions = 0;

        // Check each digit of the guess against the secret number
        for (let i = 0; i < 4; i++) {
            if (guess[i] === secretNumber[i]) {
                correctPositions++;
            } else if (secretNumber.includes(guess[i])) {
                correctDigits++;
            }
        }

        // Feedback message
        if (correctPositions === 4) {
            setFeedback("Congratulations! You guessed the secret number!");
        } else {
            setFeedback(
                `${correctPositions} digits are correct and in the correct position. ${correctDigits} digits are correct but in the wrong position.`
            );
        }
    };

    return (
        <div className="App">
            <h1>Number Position Game</h1>
            <p>Guess the secret 4-digit number!</p>

            <div className="input-section">
                <label>
                    Enter your guess:
                    <input
                        type="text"
                        value={guess}
                        onChange={(e) => setGuess(e.target.value)}
                        maxLength={4}
                    />
                </label>
                <button onClick={handleGuessSubmit}>Submit Guess</button>
            </div>

            {feedback && <p>{feedback}</p>}

            <p>Number of attempts: {attempts}</p>
        </div>
    );
}

export default App;
