import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './JoinGame.css';

const JoinGame = () => {
    const { state } = useLocation();
    const [gameId, setGameId] = useState('');
    const [playerName] = useState(state?.playerName || '');
    const [gameStatus, setGameStatus] = useState('waiting');
    const navigate = useNavigate();

    useEffect(() => {
        if (gameId) {
            axios
                .get(`http://localhost:5000/api/games/${gameId}`)
                .then(response => {
                    setGameStatus(response.data.status); // Get game status
                })
                .catch(err => {
                    console.error('Error fetching game data:', err.response || err);
                });
        }
    }, [gameId]);

    const handleJoin = () => {
        if (!gameId) {
            alert('Please enter a game code!');
            return;
        }

        // Once the second player joins, update the game status to active
        axios
            .put(`http://localhost:5000/api/games/${gameId}`, { players: [playerName], status: 'active' })
            .then(() => {
                navigate(`/game/${gameId}`, { state: { playerName } });
            })
            .catch(err => {
                console.error('Error updating game data:', err.response || err);
            });
    };

    return (
        <div className="join-game-container">
            <h1>Join a Game</h1>
            <p>Enter the game code to join:</p>
            <input
                className="input-field"
                type="text"
                placeholder="Enter game code"
                value={gameId}
                onChange={(e) => setGameId(e.target.value)}
            />
            <button className="button" onClick={handleJoin}>Join Game</button>

            {gameStatus === 'waiting' && <p>Waiting for the game to start...</p>}
            {gameStatus === 'active' && (
                <div>
                    <h2>Set a secret number (4-6 unique digits):</h2>
                    <input
                        type="text"
                        placeholder="Enter secret number"
                    />
                    <button>Set Secret Number</button>
                </div>
            )}
        </div>
    );
};

export default JoinGame;
