// src/components/GamePageCreator.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import NumberHelper from './NumberHelper';
import './GamePageCreator.css';
import { ReactComponent as ConfettiIcon } from '../assets/confetti.svg';

const GamePageCreator = () => {
    const { gameId } = useParams();
    const navigate = useNavigate();
    const [gameData, setGameData] = useState(null);
    const [guess, setGuess] = useState('');
    const [guessMessage, setGuessMessage] = useState('');

    useEffect(() => {
        const interval = setInterval(async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/games/${gameId}`);
                setGameData(res.data);
            } catch (err) {
                console.error('Error fetching game data:', err);
            }
        }, 3000);
        return () => clearInterval(interval);
    }, [gameId]);

    if (!gameData) return <div className="loading">Loading game data...</div>;

    const myGuesses = (gameData.guesses ?? []).filter(g => g.player === gameData.players[0]);
    const oppGuesses = (gameData.guesses ?? []).filter(g => g.player === gameData.players[1]);
    const playerName = gameData.players[0];
    const myTurn = gameData.turn === playerName;
    const finished = gameData.status === 'finished';

    const handleSubmitGuess = async (e) => {
        e.preventDefault();
        if (new Set(guess).size !== 4) {
            setGuessMessage('Error: Numbers must be unique');
            return;
        }
        try {
            const res = await axios.post('http://localhost:5000/api/games/guess', {
                gameId,
                player: playerName,
                guess
            });
            setGuessMessage(res.data.message);
            setGuess('');
        } catch (err) {
            setGuessMessage(err.response?.data?.message || 'Error submitting guess');
        }
    };

    return (
        <div className="game-container">
            <nav className="nav-bar">
                <h1 className="logo">NUMBER<span className="logo-accent">POSITION</span></h1>
            </nav>

            <main className="game-main">
                <div className="game-header">
                    <h2 className="game-title">Game: <span className="code">{gameId}</span></h2>
                    <div className="status-badge">
                        {finished ? 'Game Over' : myTurn ? 'Your Turn' : 'Opponent\'s Turn'}
                    </div>
                </div>

                {finished && (
                    <div className="outcome-banner">
                        {gameData.winner === "draw" ? "ü§ù Draw!" :
                            gameData.winner === playerName ? <><ConfettiIcon className="confetti" /> You Win!</> : "üíî You Lost"}
                        <button className="btn home-btn" onClick={() => navigate('/')}>Home</button>
                    </div>
                )}

                <div className="secret-code-display">
                    Your Code: <span className="code">{gameData.secretCode1}</span>
                </div>

                <div className="guess-history-container">
                    <div className="guess-column">
                        <h3 className="column-title">Your Guesses</h3>
                        <div className="guess-table">
                            <div className="table-header">
                                <div>Guess</div><div>#</div><div>Pos</div>
                            </div>
                            {myGuesses.map((g, i) => (
                                <div className="table-row" key={i}>
                                    <div>{g.guess}</div>
                                    <div className="correct-count">{g.numbersCorrect}</div>
                                    <div className="correct-count">{g.positionsCorrect}</div>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="guess-column">
                        <h3 className="column-title">Opponent's Guesses</h3>
                        <div className="guess-table">
                            <div className="table-header">
                                <div>Guess</div><div>#</div><div>Pos</div>
                            </div>
                            {oppGuesses.map((g, i) => (
                                <div className="table-row" key={i}>
                                    <div>{g.guess}</div>
                                    <div className="correct-count">{g.numbersCorrect}</div>
                                    <div className="correct-count">{g.positionsCorrect}</div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>

                {!finished && (
                    <div className="guess-input-section">
                        {myTurn ? (
                            <form className="guess-form" onSubmit={handleSubmitGuess}>
                                <input
                                    type="text"
                                    className="guess-input"
                                    placeholder="Enter 4 unique digits"
                                    value={guess}
                                    onChange={(e) => {
                                        const val = e.target.value.replace(/\D/g, '').slice(0, 4);
                                        setGuess(val);
                                        setGuessMessage('');
                                    }}
                                    maxLength="4"
                                />
                                <button
                                    type="submit"
                                    className="btn submit-guess-btn"
                                    disabled={guess.length !== 4 || new Set(guess).size !== 4}
                                >
                                    Submit
                                </button>
                            </form>
                        ) : (
                            <div className="waiting-turn">
                                <div className="pulse-dot"></div>
                                Waiting for opponent...
                            </div>
                        )}
                        <NumberHelper />
                        {guessMessage && <div className="guess-feedback">{guessMessage}</div>}
                    </div>
                )}
            </main>
        </div>
    );
};

export default GamePageCreator;
