import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import { v4 as uuidv4 } from 'uuid';  // To generate unique game IDs

const Home = () => {
    const [playerName, setPlayerName] = useState('');
    const navigate = useNavigate();

    const handleCreateGame = () => {
        if (!playerName) {
            alert('Please enter your name!');
            return;
        }
        const gameId = uuidv4();  // Generate a unique game ID
        navigate(`/create-game`, { state: { playerName, gameId } });
    };

    const handleJoinGame = () => {
        if (!playerName) {
            alert('Please enter your name!');
            return;
        }
        navigate('/join-game', { state: { playerName } });
    };

    return (
        <div className="home-container">
            <h1 className="title">Welcome to the Game!</h1>
            <p>Start a new game or join an existing one.</p>
            <input
                className="input-field"
                type="text"
                placeholder="Enter your name"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
            />
            <div className="button-container">
                <button className="button" onClick={handleCreateGame}>Start New Game</button>
                <button className="button" onClick={handleJoinGame}>Join Existing Game</button>
            </div>
        </div>
    );
};

export default Home;
