import React, { useState, useEffect } from 'react';
import { startGame, submitGuess, listenForFeedback, listenForGameOver, listenForErrors } from './services/gameService';
import GuessBoard from './components/GuessBoard';
import Timer from './components/Timer';

function App() {
    const [gameId, setGameId] = useState('');
    const [playerSecretNumber, setPlayerSecretNumber] = useState('');
    const [playerId, setPlayerId] = useState(1);
    const [feedback, setFeedback] = useState(null);
    const [gameStatus, setGameStatus] = useState('waiting_for_player2');

    useEffect(() => {
        listenForFeedback((data) => {
            setFeedback(data.feedback);
        });

        listenForGameOver((data) => {
            alert(`${data.winner} wins!`);
        });

        listenForErrors((error) => {
            alert(error);
        });
    }, []);

    const handleStartGame = () => {
        startGame(gameId, playerSecretNumber);
    };

    const handleSubmitGuess = (guess) => {
        submitGuess(gameId, playerId, guess);
    };

    return (
        <div className="App">
            <h1>Number Position Game</h1>
            {gameStatus === 'waiting_for_player2' ? (
                <div>
                    <h2>Waiting for Player 2</h2>
                    <input
                        type="text"
                        value={gameId}
                        onChange={(e) => setGameId(e.target.value)}
                        placeholder="Enter Game ID"
                    />
                    <input
                        type="text"
                        value={playerSecretNumber}
                        onChange={(e) => setPlayerSecretNumber(e.target.value)}
                        placeholder="Enter Your Secret Number"
                    />
                    <button onClick={handleStartGame}>Start Game</button>
                </div>
            ) : (
                <div>
                    <GuessBoard onSubmitGuess={handleSubmitGuess} feedback={feedback} />
                    <Timer />
                </div>
            )}
        </div>
    );
}

export default App;
