import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './JoinGame.css';
import axios from 'axios';

const JoinGame = () => {
    const { state } = useLocation();
    const [gameId, setGameId] = useState('');
    const [playerName] = useState(state?.playerName || '');
    const [gameData, setGameData] = useState(null);
    const navigate = useNavigate();

    const handleJoin = () => {
        if (!gameId) {
            alert('Please enter a game code!');
            return;
        }
        navigate(`/game/${gameId}`, { state: { playerName } });
    };

    useEffect(() => {
        if (gameId) {
            axios
                .get(`http://localhost:5000/api/games/${gameId}`)
                .then(response => {
                    setGameData(response.data);
                })
                .catch(err => {
                    console.error('Error fetching game data:', err);
                });
        }
    }, [gameId]);

    return (
        <div className="join-game-container">
            <h1>Join a Game</h1>
            <p>Enter the game code to join:</p>
            <input
                className="input-field"
                type="text"
                placeholder="Enter game code"
                value={gameId}
                onChange={(e) => setGameId(e.target.value)}
            />
            <button className="button" onClick={handleJoin}>Join Game</button>

            {gameData && (
                <div>
                    <h2>Game Details</h2>
                    <p>Players: {gameData.players.join(', ')}</p>
                    <p>Status: {gameData.status}</p>
                </div>
            )}
        </div>
    );
};

export default JoinGame;
