import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const SetSecretCode = () => {
    const [secretCode, setSecretCode] = useState("");
    const playerName = localStorage.getItem("player1") || localStorage.getItem("player2");
    const gameId = localStorage.getItem("gameId");
    const navigate = useNavigate();

    const handleSecretCodeChange = (e) => {
        const value = e.target.value;
        if (value.length <= 4 && /^[0-9]*$/.test(value) && new Set(value).size === value.length) {
            setSecretCode(value);
        }
    };

    const handleSubmitSecretCode = () => {
        if (secretCode.length !== 4) return alert("Enter a 4-digit unique number!");

        // Store secret code locally
        localStorage.setItem(`${playerName}_secretCode`, secretCode);

        // Send to backend once both players submit
        axios.post("http://localhost:5000/api/games/setsecret", {
            gameId,
            playerName,
            secretCode
        })
            .then(response => {
                if (response.data.success) {
                    navigate(`/game/${gameId}`);
                } else {
                    alert("Waiting for the other player...");
                }
            })
            .catch(err => console.error("Error:", err));
    };

    return (
        <div>
            <h2>Set Your Secret Code</h2>
            <input type="text" placeholder="Enter 4-digit code" value={secretCode} onChange={handleSecretCodeChange} />
            <button onClick={handleSubmitSecretCode}>Submit</button>
        </div>
    );
};

export default SetSecretCode;
