import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './CreateGame.css';
import axios from 'axios';

const CreateGame = () => {
    const { state } = useLocation();
    const [playerName, setPlayerName] = useState(state ? state.playerName : '');
    const [gameId, setGameId] = useState('');
    const [playerJoined, setPlayerJoined] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (!state) {
            alert('No player information found!');
            navigate('/');
        }
    }, [state, navigate]);

    useEffect(() => {
        if (playerName && !gameId) {
            axios
                .post('http://localhost:5000/api/games/start-game', { players: [playerName] })
                .then(response => {
                    setGameId(response.data.gameId);  // Correctly set gameId
                })
                .catch(error => {
                    console.error('Error starting the game:', error);
                });
        }
    }, [playerName, gameId]);

    const handlePlayerJoin = () => {
        setPlayerJoined(true);
    };

    useEffect(() => {
        if (playerJoined && gameId) {
            navigate(`/game/${gameId}`);  // Ensure proper redirection
        }
    }, [playerJoined, gameId, navigate]);

    return (
        <div className="game-container">
            <h1>Game ID: {gameId || 'Loading...'}</h1> {/* Display real gameId */}
            <p>Welcome, {playerName}!</p>
            {!playerJoined ? (
                <p>Waiting for another player to join...</p>
            ) : (
                <p>Another player has joined! Starting game...</p>
            )}
            <button className="button" onClick={handlePlayerJoin}>
                Simulate Player Join (For Testing)
            </button>
        </div>
    );
};

export default CreateGame;
