import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./CreateGame.css";

const CreateGame = () => {
    const [playerName, setPlayerName] = useState("");
    const [gameId, setGameId] = useState("");
    const [playerJoined, setPlayerJoined] = useState(false);
    const navigate = useNavigate();

    // Generate a random 6-digit game ID
    const generateGameId = () => Math.floor(100000 + Math.random() * 900000).toString();

    // Create game locally (P1)
    const handleCreateGame = () => {
        if (!playerName) return alert("Enter your name");

        const newGameId = generateGameId();
        setGameId(newGameId);
        localStorage.setItem("gameId", newGameId);
        localStorage.setItem("player1", playerName);
    };

    // Join existing game (P2)
    const handleJoinGame = () => {
        if (!gameId || !playerName) return alert("Enter game ID and name");

        localStorage.setItem("player2", playerName);

        // Send game details to backend for approval
        axios.post("http://localhost:5000/api/games/start", { gameId, players: [localStorage.getItem("player1"), playerName] })
            .then(response => {
                if (response.data.success) {
                    setPlayerJoined(true);
                    navigate(`/${playerName}/setsecretcode`);
                } else {
                    alert("Error starting the game");
                }
            })
            .catch(err => console.error("Error:", err));
    };

    return (
        <div className="game-container">
            <h1>Number Position Game</h1>
            <input type="text" placeholder="Enter your name" value={playerName} onChange={(e) => setPlayerName(e.target.value)} />

            {!gameId ? (
                <button onClick={handleCreateGame}>Create Game</button>
            ) : (
                <>
                    <p>Game ID: {gameId}</p>
                    <input type="text" placeholder="Enter game ID" value={gameId} onChange={(e) => setGameId(e.target.value)} />
                    <button onClick={handleJoinGame}>Join Game</button>
                </>
            )}

            {playerJoined && <p>Game is starting...</p>}
        </div>
    );
};

export default CreateGame;
