// src/components/SecretCodePage.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const SecretCodePage = () => {
    const { playerName, gameId } = useParams();
    const navigate = useNavigate();
    const [secretCode, setSecretCode] = useState('');
    const [message, setMessage] = useState('');

    const handleSetSecretCode = async () => {
        if (secretCode.length !== 4) {
            alert('Secret code must be exactly 4 digits.');
            return;
        }
        try {
            const response = await axios.post('http://localhost:5000/api/games/setsecret', { gameId, playerName, secretCode });
            setMessage(response.data.message);
            // If both players have set their secret codes, the game status will be "ready"
            if (response.data.game.status === 'ready') {
                navigate(`/game/${gameId}`);
            }
        } catch (err) {
            console.error('Error setting secret code:', err);
            setMessage('Error setting secret code');
        }
    };

    // Optionally, poll the game status if waiting for the other player’s secret code
    useEffect(() => {
        const interval = setInterval(async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/games/${gameId}`);
                const game = res.data;
                if (game.status === 'ready') {
                    navigate(`/game/${gameId}`);
                }
            } catch (err) {
                console.error('Error polling game status:', err);
            }
        }, 3000);
        return () => clearInterval(interval);
    }, [gameId, navigate]);

    return (
        <div>
            <h2>Set Your Secret Code</h2>
            <p>Player: {playerName}</p>
            <p>Game Code: {gameId}</p>
            <input
                type="text"
                placeholder="Enter 4 unique digits"
                value={secretCode}
                onChange={(e) => setSecretCode(e.target.value)}
                maxLength={4}
            />
            <button onClick={handleSetSecretCode}>Set Secret Code</button>
            {message && <p>{message}</p>}
        </div>
    );
};

export default SecretCodePage;
