import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const JoinGame = ({ gameId }) => {
    const [playerName, setPlayerName] = useState('');
    const [message, setMessage] = useState('');
    const [gameData, setGameData] = useState(null);
    const navigate = useNavigate();

    const handleJoinGame = () => {
        if (!playerName) {
            console.error('Player name is not defined!');
            setMessage('Player name is required!');
            return;
        }

        if (gameData && gameData.players.length === 2) {
            console.log('Game already has two players');
            setMessage('Game already has two players.');
            return;
        }

        console.log('Joining game with the following data:', { gameId, playerName });

        // Send request to join the game using axios
        axios.post('http://localhost:5000/api/games/join', { gameId, player: playerName })
            .then(response => {
                const data = response.data;
                if (data.game) {
                    console.log('Player joined the game:', data);
                    setMessage('Player joined the game!');
                    setGameData(data.game);

                    // If this is the second player, navigate to the secret code page
                    if (data.game.players.length === 2) {
                        setMessage('Both players are now in the game. The game will start.');
                        // Redirect to the correct page for the second player
                        navigate(`/secretcode/j/${gameId}`, { state: { playerName, role: 'joiner' } });
                    }
                } else {
                    console.log(data.message);
                    setMessage(data.message);
                }
            })
            .catch(err => {
                console.error('Error joining the game:', err);
                setMessage('Error joining the game');
            });
    };

    return (
        <div>
            <h2>Join Game</h2>
            <input
                type="text"
                placeholder="Enter your name"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
            />
            <button onClick={handleJoinGame}>Join Game</button>
            {message && <p>{message}</p>}
        </div>
    );
};

export default JoinGame;
