import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const GamePage = () => {
    const { gameId } = useParams();  // Get the gameId from the URL
    const [gameData, setGameData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Check if gameId exists before making the request
        if (!gameId) {
            setLoading(false);
            return;
        }

        // Fetch game data
        axios.get(`/api/games/${gameId}`)
            .then(response => {
                setGameData(response.data); // Set the game data
                setLoading(false); // Set loading to false once data is fetched
            })
            .catch(error => {
                console.error('Error fetching game data:', error);
                setLoading(false); // Set loading to false if there's an error
            });
    }, [gameId]);

    if (loading) {
        return <div>Loading game...</div>;
    }

    if (!gameData) {
        return <div>Error loading game. Please try again later.</div>;
    }

    return (
        <div>
            <h1>Game ID: {gameId}</h1>
            <h2>Game Status: {gameData.status}</h2>
            <p>Player Count: {gameData.players.length}</p>
            {/* Add more details and controls for the game */}
        </div>
    );
};

export default GamePage;
