import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const JoinGame = ({ gameId }) => {
    const [playerName, setPlayerName] = useState("");
    const [message, setMessage] = useState("");
    const navigate = useNavigate();

    const handleJoinGame = async () => {
        if (!playerName) {
            setMessage("Player name is required!");
            return;
        }

        try {
            const response = await axios.post("http://localhost:5000/api/games/join", {
                gameId,
                player: playerName
            });

            const data = response.data;

            if (data.redirectUrl) {
                setMessage("Player joined successfully! Redirecting...");
                navigate(data.redirectUrl, { state: { playerName, role: "joiner" } });
            } else {
                setMessage(data.message);
            }
        } catch (err) {
            console.error("Error joining the game:", err);
            setMessage("Error joining the game. Please try again.");
        }
    };

    return (
        <div>
            <h2>Join Game</h2>
            <input
                type="text"
                placeholder="Enter your name"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
            />
            <button onClick={handleJoinGame}>Join Game</button>
            {message && <p>{message}</p>}
        </div>
    );
};

export default JoinGame;
