import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import NumberHelper from './NumberHelper';
import Timer from './Timer';
import './GamePageJoiner.css';
import { ReactComponent as ConfettiIcon } from '../assets/confetti.svg';

const GamePageJoiner = () => {
    const { gameId } = useParams();
    const navigate = useNavigate();
    const [gameData, setGameData] = useState(null);
    const [guess, setGuess] = useState('');
    const [guessMessage, setGuessMessage] = useState('');
    const [turnTrigger, setTurnTrigger] = useState(null);
    const [question, setQuestion] = useState('');
    const [answerOptions, setAnswerOptions] = useState([]);

    useEffect(() => {
        const interval = setInterval(async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/games/${gameId}`);
                setGameData(res.data);
                setTurnTrigger(res.data.turn);
            } catch (err) {
                console.error('Error fetching game data:', err);
            }
        }, 3000);
        return () => clearInterval(interval);
    }, [gameId]);

    useEffect(() => {
        if (gameData && gameData.turn !== gameData.players[1]) {
            fetchTriviaOrWouldYouRather();
        }
    }, [gameData]);

    const fetchTriviaOrWouldYouRather = async () => {
        try {
            const triviaRes = await axios.get('https://opentdb.com/api.php?amount=1&type=multiple');
            const question = triviaRes.data.results[0];
            setQuestion(question.question);
            setAnswerOptions([...question.incorrect_answers, question.correct_answer].sort());
        } catch (err) {
            console.error('Error fetching trivia:', err);
        }
    };

    if (!gameData) return <p>Loading game data...</p>;

    const myGuesses = (gameData.guesses ?? []).filter(g => g.player === gameData.players[1]);
    const oppGuesses = (gameData.guesses ?? []).filter(g => g.player === gameData.players[0]);
    const playerName = gameData.players[1];
    const myTurn = gameData.turn === playerName;

    const handleSubmitGuess = async (e) => {
        e.preventDefault();
        if (!guess) {
            alert('Please enter a guess.');
            return;
        }
        try {
            const res = await axios.post('http://localhost:5000/api/games/guess', { gameId, player: playerName, guess });
            setGuessMessage(res.data.message);
            setGuess('');
        } catch (err) {
            console.error('Error submitting guess:', err);
            setGuessMessage(err.response?.data?.message || 'Error submitting guess');
        }
    };

    const onTimeUp = async () => {
        if (myTurn && !gameData.status === 'finished') {
            try {
                const res = await axios.post('http://localhost:5000/api/games/guess', { gameId, player: playerName, guess: '' });
                setGuessMessage('Time is up! Turn skipped.');
            } catch (err) {
                console.error('Error auto-submitting guess:', err);
            }
        }
    };

    const finished = gameData.status === 'finished';
    let outcomeMessage = '';
    if (finished) {
        if (gameData.winner === "draw") {
            outcomeMessage = "It's a draw!";
        } else if (gameData.winner === playerName) {
            outcomeMessage = "Congratulations! You win! Confetti!!!";
        } else {
            outcomeMessage = "You lost! Better luck next time!";
        }
    }

    return (
        <div>
            <h2>Player 2 Game Page</h2>
            <p>Your Secret Code: {gameData.secretCode2}</p>
            <p><strong>Turn:</strong> {gameData.turn ? gameData.turn : 'Not set'}</p>
            {!finished && <Timer initialTime={120} onTimeUp={onTimeUp} resetTrigger={turnTrigger} />}
            {finished && (
                <div>
                    <h3>Game Over</h3>
                    <p>{outcomeMessage}</p>
                    <button onClick={() => navigate('/')}>Go to Home Page</button>
                </div>
            )}
            {!finished && (
                <>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <div style={{ width: '48%' }}>
                            <h3>Your Guess History</h3>
                            {myGuesses.length === 0 ? (
                                <p>No guesses yet.</p>
                            ) : (
                                <table border="1">
                                    <thead>
                                        <tr>
                                            <th>Guess</th>
                                            <th>Numbers Correct</th>
                                            <th>Positions Correct</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {myGuesses.map((g, index) => (
                                            <tr key={index}>
                                                <td>{g.guess}</td>
                                                <td>{g.numbersCorrect}</td>
                                                <td>{g.positionsCorrect}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            )}
                        </div>
                        <div style={{ width: '48%' }}>
                            <h3>Opponent's Guess History</h3>
                            {oppGuesses.length === 0 ? (
                                <p>No guesses yet.</p>
                            ) : (
                                <table border="1">
                                    <thead>
                                        <tr>
                                            <th>Guess</th>
                                            <th>Numbers Correct</th>
                                            <th>Positions Correct</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {oppGuesses.map((g, index) => (
                                            <tr key={index}>
                                                <td>{g.guess}</td>
                                                <td>{g.numbersCorrect}</td>
                                                <td>{g.positionsCorrect}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            )}
                        </div>
                    </div>
                    {myTurn ? (
                        <form onSubmit={handleSubmitGuess}>
                            <input
                                type="text"
                                placeholder="Enter your guess"
                                value={guess}
                                onChange={(e) => setGuess(e.target.value)}
                            />
                            <button type="submit">Submit Guess</button>
                        </form>
                    ) : (
                        <>
                            <p>Waiting for your turn...</p>
                            <div>
                                <h3>Trivia Question:</h3>
                                <p>{question}</p>
                                <ul>
                                    {answerOptions.map((option, index) => (
                                        <li key={index}>{option}</li>
                                    ))}
                                </ul>
                            </div>
                        </>
                    )}
                    <NumberHelper />
                    {guessMessage && <p>{guessMessage}</p>}
                </>
            )}
        </div>
    );
};

export default GamePageJoiner;
