// src/components/NumberHelper.js
import React, { useState } from 'react';

const NumberHelper = () => {
    const initialState = {};
    for (let i = 0; i < 10; i++) {
        initialState[i] = false;
    }
    const [eliminated, setEliminated] = useState(initialState);

    const toggleDigit = (digit) => {
        setEliminated({ ...eliminated, [digit]: !eliminated[digit] });
    };

    return (
        <div style={{ marginTop: '10px' }}>
            <p>Eliminate numbers you know are not in your opponent's secret code:</p>
            {Array.from({ length: 10 }, (_, i) => (
                <button
                    key={i}
                    onClick={() => toggleDigit(i)}
                    style={{
                        margin: '2px',
                        padding: '5px',
                        position: 'relative',
                        backgroundColor: '#fff'
                    }}
                >
                    {i}
                    {eliminated[i] && (
                        <span
                            style={{
                                position: 'absolute',
                                top: '0px',
                                left: '0px',
                                color: 'red',
                                fontWeight: 'bold'
                            }}
                        >
                            Ã˜
                        </span>
                    )}
                </button>
            ))}
        </div>
    );
};

export default NumberHelper;
