import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const JoinGame = ({ gameId }) => {
    const [playerName, setPlayerName] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    const handleJoinGame = () => {
        if (!playerName) {
            console.error('Player name is not defined!');
            setMessage('Player name is required!');
            return;
        }

        console.log('Attempting to join game with:', { gameId, playerName });

        axios.post('http://localhost:5000/api/games/join', { gameId, player: playerName })
            .then(response => {
                const data = response.data;
                console.log('Join response:', data);
                if (data.game) {
                    setMessage('Player joined the game successfully!');

                    // Navigate to secret code setup page for Player 2
                    navigate(`/secretcode/j/${gameId}`, { state: { playerName, role: 'joiner' } });
                } else {
                    setMessage(data.message || 'Unexpected error');
                }
            })
            .catch(err => {
                console.error('Error joining the game:', err);
                setMessage('Error joining the game');
            });
    };

    return (
        <div>
            <h2>Join Game</h2>
            <input
                type="text"
                placeholder="Enter your name"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
            />
            <button onClick={handleJoinGame}>Join Game</button>
            {message && <p>{message}</p>}
        </div>
    );
};

export default JoinGame;
