import io from 'socket.io-client';

// Connect to the server
const socket = io();

// Start a new game
export const startGame = (gameId, playerSecretNumber) => {
    socket.emit('start-game', gameId, playerSecretNumber);
};

// Submit a guess
export const submitGuess = (gameId, playerId, guess) => {
    socket.emit('submit-guess', gameId, playerId, guess);
};

// Listen for feedback (N/P)
export const listenForFeedback = (callback) => {
    socket.on('guess-feedback', callback);
};

// Listen for game over
export const listenForGameOver = (callback) => {
    socket.on('game-over', callback);
};

// Handle errors
export const listenForErrors = (callback) => {
    socket.on('error', callback);
};

export const socketInstance = socket;
