import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './CreateGame.css';
import axios from 'axios';

const CreateGame = () => {
    const { state } = useLocation();
    const [playerName, setPlayerName] = useState(state ? state.playerName : '');
    const [gameId, setGameId] = useState('');
    const [playerJoined, setPlayerJoined] = useState(false);
    const [secretNumber, setSecretNumber] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        if (!state) {
            alert('No player information found!');
            navigate('/');
        }
    }, [state, navigate]);

    useEffect(() => {
        if (playerName && !gameId) {
            console.log('Starting game...');
            axios.post('http://localhost:5000/api/games/start-game', { players: [playerName] })
                .then(response => {
                    setGameId(response.data.gameId);
                })
                .catch(error => {
                    console.error('Error starting the game:', error);
                });
        }
    }, [playerName, gameId]);

    const handleSetSecretNumber = () => {
        if (secretNumber.length !== 4 || new Set(secretNumber).size !== 4) {
            alert('Secret number must be 4 unique digits.');
            return;
        }
        axios.post('http://localhost:5000/api/games/setSecretNumber', {
            gameId, player: 'player1', secretNumber
        })
            .then(() => alert('Secret number set successfully!'))
            .catch(err => alert('Error setting secret number: ' + err.response.data.message));
    };

    return (
        <div className="game-container">
            <h1>Game ID: {gameId || 'Loading...'}</h1>
            <p>Welcome, {playerName}!</p>
            {!playerJoined ? (
                <div>
                    <input
                        type="text"
                        value={secretNumber}
                        onChange={(e) => setSecretNumber(e.target.value)}
                        maxLength={4}
                        placeholder="Enter 4-digit secret number"
                    />
                    <button className="button" onClick={handleSetSecretNumber}>
                        Set Secret Number
                    </button>
                </div>
            ) : (
                <p>Another player has joined! Starting game...</p>
            )}
            <button className="button" onClick={() => setPlayerJoined(true)}>
                Simulate Player Join (For Testing)
            </button>
        </div>
    );
};

export default CreateGame;
