import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const GamePage = () => {
    const { gameId } = useParams();  // Extract the gameId from the URL
    const [gameData, setGameData] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get(`/api/games/${gameId}`)  // Make sure this URL is correct
            .then(response => {
                setGameData(response.data);  // Store the game data
            })
            .catch(err => {
                setError('Error fetching game data');
                console.error(err);
            });
    }, [gameId]);  // Re-fetch if gameId changes

    if (error) {
        return <div>{error}</div>;
    }

    if (!gameData) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Game: {gameData.gameId}</h1>
            <p>Players: {gameData.players.join(', ')}</p>
            <p>Status: {gameData.status}</p>
            <p>Guesses: {gameData.guesses.length}</p>
        </div>
    );
};

export default GamePage;
